      .sort((a, b) => (b.createdAt?.getTime() || 0) - (a.createdAt?.getTime() || 0))
      .slice(offset, offset + limit);
    return userBets;
  }

  async getUserBetsByTeam(userId: string, teamName: string): Promise<UserBet[]> {
