      'Minnesota Twins': { runFactor: 97, hrFactor: 95 },
      'Chicago White Sox': { runFactor: 96, hrFactor: 97 },
      'Pittsburgh Pirates': { runFactor: 96, hrFactor: 94 },
      'Detroit Tigers': { runFactor: 95, hrFactor: 93 },
