interface OddsApiResponse {
  timestamp: string;
  previous_timestamp?: string;
  next_timestamp?: string;
  data: any[];
}

interface Game {
  id: string;
  sport_key: string;
  sport_title: string;
  commence_time: string;
  home_team: string;
  away_team: string;
  bookmakers: Bookmaker[];
}

interface Bookmaker {
  key: string;
  title: string;
  last_update: string;
  link?: string;  // Event-level deep link
  sid?: string;   // Source ID for event
  markets: Market[];
}

interface Market {
  key: string;
  link?: string;   // Market-level deep link
