      market: 'moneyline',
      selection: '',
      line: '',
      odds: '',
      units: 1
    });
  };

  const formatOdds = (odds: number) => {
    return odds > 0 ? `+${odds}` : `${odds}`;
  };



  const getBetRecommendation = () => {
    if (!prediction) return null;

