import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { VisuallyHidden } from "@radix-ui/react-visually-hidden";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { User, Clock, MapPin } from "lucide-react";
import { useQuery } from "@tanstack/react-query";

interface GameDetailsModalProps {
  isOpen: boolean;
  onClose: () => void;
  gameId: string | number;
  homeTeam: string;
  awayTeam: string;
  startTime?: string;
  venue?: string;
  probablePitchers?: {
    home: string | null;
    away: string | null;
  };
}

interface Lineup {
  home: Array<{
    id: number;
    name: string;
    position: string;
    battingOrder: number;
  }>;
  away: Array<{
    id: number;
    name: string;
    position: string;
    battingOrder: number;
  }>;
}

export function GameDetailsModal({
  isOpen,
  onClose,
  gameId,
  homeTeam,
  awayTeam,
  startTime,
  venue,
  probablePitchers
}: GameDetailsModalProps) {
  const { data: lineups, isLoading: lineupsLoading } = useQuery<Lineup>({
    queryKey: ['/api/mlb/game', gameId, 'lineups'],
    enabled: isOpen && !!gameId,
  });

  const formatPosition = (pos: string) => {
    const positions: Record<string, string> = {
      'P': 'Pitcher', 'C': 'Catcher', '1B': 'First Base', '2B': 'Second Base',
      '3B': 'Third Base', 'SS': 'Shortstop', 'LF': 'Left Field', 'CF': 'Center Field',
      'RF': 'Right Field', 'DH': 'Designated Hitter'
    };
    return positions[pos] || pos;
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-xl font-bold">
            {awayTeam} @ {homeTeam}
          </DialogTitle>
          <VisuallyHidden>
            <DialogDescription>
              Game details and lineups for {awayTeam} at {homeTeam} including probable pitchers and starting lineups.
            </DialogDescription>
          </VisuallyHidden>
          <div className="flex items-center gap-4 text-sm text-gray-600 dark:text-gray-400">
            {startTime && (
              <div className="flex items-center gap-1">
                <Clock className="w-4 h-4" />
                {startTime}
              </div>
            )}
            {venue && (
              <div className="flex items-center gap-1">
                <MapPin className="w-4 h-4" />
                {venue}
              </div>
            )}
          </div>
        </DialogHeader>

        <div className="space-y-6">
          {/* Probable Pitchers */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Probable Pitchers</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-2 gap-6">
                <div className="text-center">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-2">{awayTeam}</h4>
                  <div className="flex items-center justify-center gap-2">
                    <User className="w-4 h-4 text-gray-500" />
                    <span className="text-sm">
                      {probablePitchers?.away || 'TBD'}
                    </span>
                  </div>
                </div>
                <div className="text-center">
                  <h4 className="font-medium text-gray-900 dark:text-white mb-2">{homeTeam}</h4>
                  <div className="flex items-center justify-center gap-2">
                    <User className="w-4 h-4 text-gray-500" />
                    <span className="text-sm">
                      {probablePitchers?.home || 'TBD'}
                    </span>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Lineups */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Starting Lineups</CardTitle>
            </CardHeader>
            <CardContent>
              {lineupsLoading ? (
                <div className="text-center py-8">
                  <p className="text-gray-500 dark:text-gray-400">Loading lineups...</p>
                </div>
              ) : lineups && (lineups.home.length > 0 || lineups.away.length > 0) ? (
                <div className="grid grid-cols-2 gap-6">
                  {/* Away Team Lineup */}
                  <div>
                    <h4 className="font-medium text-gray-900 dark:text-white mb-3">{awayTeam}</h4>
                    <div className="space-y-2">
                      {lineups.away.length > 0 ? lineups.away.map((player, index) => (
                        <div key={player.id} className="flex items-center justify-between text-sm">
                          <div className="flex items-center gap-2">
                            <Badge variant="outline" className="w-6 h-6 text-xs p-0 flex items-center justify-center">
                              {player.battingOrder || index + 1}
                            </Badge>
                            <span className="font-medium">{player.name}</span>
                          </div>
                          <span className="text-gray-500 dark:text-gray-400 text-xs">
                            {formatPosition(player.position)}
                          </span>
                        </div>
                      )) : (
                        <p className="text-gray-500 dark:text-gray-400 text-sm">Lineup TBD</p>
                      )}
                    </div>
                  </div>

                  {/* Home Team Lineup */}
                  <div>
                    <h4 className="font-medium text-gray-900 dark:text-white mb-3">{homeTeam}</h4>
                    <div className="space-y-2">
                      {lineups.home.length > 0 ? lineups.home.map((player, index) => (
                        <div key={player.id} className="flex items-center justify-between text-sm">
                          <div className="flex items-center gap-2">
                            <Badge variant="outline" className="w-6 h-6 text-xs p-0 flex items-center justify-center">
                              {player.battingOrder || index + 1}
                            </Badge>
                            <span className="font-medium">{player.name}</span>
                          </div>
                          <span className="text-gray-500 dark:text-gray-400 text-xs">
                            {formatPosition(player.position)}
                          </span>
                        </div>
                      )) : (
                        <p className="text-gray-500 dark:text-gray-400 text-sm">Lineup TBD</p>
                      )}
                    </div>
                  </div>
                </div>
              ) : (
                <div className="text-center py-8">
                  <p className="text-gray-500 dark:text-gray-400">Lineups TBD</p>
                  <p className="text-xs text-gray-400 dark:text-gray-500 mt-1">
                    Lineups are typically posted 1-2 hours before game time
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/LiveMLBGames.tsx.content:
import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { RefreshCw, Calendar, Clock, Users, TrendingUp } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';

interface LiveGame {
  gamePk: number;
  gameDate: string;
  status: { detailedState: string };
  teams: {
    home: {
      team: { name: string; id: number };
      probablePitcher?: { fullName: string; id: number };
    };
    away: {
      team: { name: string; id: number };
      probablePitcher?: { fullName: string; id: number };
    };
  };
  venue: { name: string };
}

interface LivePrediction {
  game: {
    homeTeam: string;
    awayTeam: string;
    gameDate: string;
    probableStarters: {
      home: string;
      away: string;
    };
  };
  prediction: {
    homeWinProbability: number;
    awayWinProbability: number;
    overProbability: number;
    underProbability: number;
    predictedTotal: number;
    confidence: number;
  };
  starterStats: {
    home: any;
    away: any;
  };
}

export function LiveMLBGames() {
  const [games, setGames] = useState<LiveGame[]>([]);
  const [predictions, setPredictions] = useState<{ [gameId: number]: LivePrediction }>({});
  const [loading, setLoading] = useState(false);
  const [updating2025Data, setUpdating2025Data] = useState(false);

  const fetchTodaysGames = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/baseball/todays-games');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setGames(data);
    } catch (error) {
      console.error('Failed to fetch today\'s games:', error);
    } finally {
      setLoading(false);
    }
  };

  const getPrediction = async (gameId: number) => {
    try {
      const response = await fetch(`/api/baseball/live-prediction/${gameId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setPredictions(prev => ({ ...prev, [gameId]: data }));
    } catch (error) {
      console.error(`Failed to get prediction for game ${gameId}:`, error);
    }
  };

  const update2025Data = async () => {
    setUpdating2025Data(true);
    try {
      const response = await fetch('/api/baseball/update-2025-data', { method: 'POST' });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      await response.json();
      alert('2025 season data updated successfully! Model now includes current season performance.');
    } catch (error) {
      console.error('Failed to update 2025 data:', error);
      alert('Failed to update 2025 data. Please try again.');
    } finally {
      setUpdating2025Data(false);
    }
  };

  useEffect(() => {
    fetchTodaysGames();
  }, []);

  const formatGameTime = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('en-US', { 
      hour: 'numeric', 
      minute: '2-digit',
      timeZoneName: 'short'
    });
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="h-5 w-5" />
            Today's MLB Games
          </CardTitle>
          <CardDescription>
            Live games with AI predictions and probable starters
          </CardDescription>
          <div className="flex gap-2">
            <Button onClick={fetchTodaysGames} disabled={loading} variant="outline" size="sm">
              <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
              Refresh Games
            </Button>
            <Button onClick={update2025Data} disabled={updating2025Data} variant="outline" size="sm">
              <TrendingUp className={`h-4 w-4 mr-2 ${updating2025Data ? 'animate-spin' : ''}`} />
              Update 2025 Data
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          {games.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-muted-foreground">
                {loading ? 'Loading today\'s games...' : 'No games scheduled for today'}
              </p>
            </div>
          ) : (
            <div className="grid gap-4">
              {games.map((game) => {
                const prediction = predictions[game.gamePk];

                return (
                  <Card key={game.gamePk} className="border-l-4 border-l-blue-500">
                    <CardContent className="pt-6">
                      <div className="space-y-4">
                        {/* Game Header */}
                        <div className="flex items-center justify-between">
                          <div className="space-y-1">
                            <h3 className="font-semibold text-lg">
                              {game.teams.away.team.name} @ {game.teams.home.team.name}
                            </h3>
                            <div className="flex items-center gap-4 text-sm text-muted-foreground">
                              <span className="flex items-center gap-1">
                                <Clock className="h-4 w-4" />
                                {formatGameTime(game.gameDate)}
                              </span>
                              <span className="flex items-center gap-1">
                                <Users className="h-4 w-4" />
                                {game.venue.name}
                              </span>
                            </div>
                          </div>
                          <Badge variant={game.status.detailedState === 'Scheduled' ? 'default' : 'secondary'}>
                            {game.status.detailedState}
                          </Badge>
                        </div>

                        {/* Probable Starters */}
                        <div className="grid grid-cols-2 gap-4 p-3 bg-muted/50 rounded-lg">
                          <div>
                            <p className="text-sm font-medium">Away Starter</p>
                            <p className="text-sm text-muted-foreground">
                              {game.teams.away.probablePitcher?.fullName || 'TBD'}
                            </p>
                          </div>
                          <div>
                            <p className="text-sm font-medium">Home Starter</p>
                            <p className="text-sm text-muted-foreground">
                              {game.teams.home.probablePitcher?.fullName || 'TBD'}
                            </p>
                          </div>
                        </div>

                        {/* Prediction Section */}
                        {prediction ? (
                          <div className="space-y-3">
                            <div className="grid grid-cols-2 gap-4">
                              <div className="text-center p-3 bg-green-50 dark:bg-green-950 rounded-lg">
                                <p className="text-sm font-medium text-green-700 dark:text-green-300">
                                  {prediction.game.awayTeam}
                                </p>
                                <p className="text-xl font-bold text-green-600 dark:text-green-400">
                                  {(prediction.prediction.awayWinProbability * 100).toFixed(1)}%
                                </p>
                              </div>
                              <div className="text-center p-3 bg-blue-50 dark:bg-blue-950 rounded-lg">
                                <p className="text-sm font-medium text-blue-700 dark:text-blue-300">
                                  {prediction.game.homeTeam}
                                </p>
                                <p className="text-xl font-bold text-blue-600 dark:text-blue-400">
                                  {(prediction.prediction.homeWinProbability * 100).toFixed(1)}%
                                </p>
                              </div>
                            </div>

                            <div className="grid grid-cols-3 gap-2 text-center text-sm">
                              <div>
                                <p className="text-muted-foreground">Predicted Total</p>
                                <p className="font-medium">{prediction.prediction.predictedTotal.toFixed(1)}</p>
                              </div>
                              <div>
                                <p className="text-muted-foreground">Over</p>
                                <p className="font-medium">{(prediction.prediction.overProbability * 100).toFixed(1)}%</p>
                              </div>
                              <div>
                                <p className="text-muted-foreground">Confidence</p>
                                <p className="font-medium">{(prediction.prediction.confidence * 100).toFixed(1)}%</p>
                              </div>
                            </div>
                          </div>
                        ) : (
                          <Button 
                            onClick={() => getPrediction(game.gamePk)} 
                            variant="outline" 
                            size="sm"
                            className="w-full"
                          >
                            Get AI Prediction
                          </Button>
                        )}
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/LiveOddsMonitor.ts.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";

interface Game {
  id: number;
  homeTeam: string;
  awayTeam: string;
  status: string;
  commenceTime: string;
}

interface LiveOddsMonitorProps {
  liveGames: Game[];
}

export default function LiveOddsMonitor({ liveGames }: LiveOddsMonitorProps) {
  const formatGameTime = (commenceTime: string, status: string) => {
    if (status === "live") {
      return `Q${Math.floor(Math.random() * 4) + 1} ${Math.floor(Math.random() * 15) + 1}:${Math.floor(Math.random() * 60).toString().padStart(2, '0')}`;
    }

    const time = new Date(commenceTime);
    return time.toLocaleTimeString('en-US', { 
      hour: 'numeric', 
      minute: '2-digit',
      timeZone: 'America/New_York',
      timeZoneName: 'short'
    });
  };

  const mockOdds = () => ({
    moneyline: {
      home: Math.floor(Math.random() * 200) - 100,
      away: Math.floor(Math.random() * 200) - 100,
    },
    total: {
      points: (Math.random() * 20 + 40).toFixed(1),
      odds: -110
    },
    edge: (Math.random() * 8 + 1).toFixed(1)
  });

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg font-semibold text-gray-900 dark:text-white">
            Live Odds Monitor
          </CardTitle>
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-error rounded-full animate-pulse"></div>
            <span className="text-sm text-gray-500 dark:text-gray-400">Real-time</span>
          </div>
        </div>
      </CardHeader>

      <CardContent>
        <div className="space-y-4">
          {liveGames.length === 0 ? (
            <>
              {/* Mock live games when no real data */}
              <div className="border border-gray-200 dark:border-gray-700 rounded-lg p-4">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-2">
                    <span className="text-sm font-medium text-gray-900 dark:text-white">
                      Chiefs @ Raiders
                    </span>
                    <Badge variant="destructive" className="text-xs">LIVE</Badge>
                  </div>
                  <span className="text-sm text-gray-500 dark:text-gray-400">Q2 14:32</span>
                </div>

                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <div className="text-gray-600 dark:text-gray-400 mb-1">Moneyline</div>
                    <div className="font-medium text-gray-900 dark:text-white">KC: -165 | LV: +140</div>
                  </div>
                  <div>
                    <div className="text-gray-600 dark:text-gray-400 mb-1">Total</div>
                    <div className="font-medium text-gray-900 dark:text-white">O/U 47.5 (-110)</div>
                  </div>
                </div>

                <div className="mt-3 flex items-center justify-between">
                  <div className="flex items-center space-x-2">
                    <span className="text-xs text-gray-500 dark:text-gray-400">Edge detected:</span>
                    <span className="text-xs font-medium text-success">+3.2% on Over</span>
                  </div>
                  <Button variant="link" size="sm" className="text-xs">
                    View Details
                  </Button>
                </div>
              </div>

              <div className="border border-gray-200 dark:border-gray-700 rounded-lg p-4">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-2">
                    <span className="text-sm font-medium text-gray-900 dark:text-white">
                      Cowboys @ Eagles
                    </span>
                    <Badge variant="secondary" className="text-xs">UPCOMING</Badge>
                  </div>
                  <span className="text-sm text-gray-500 dark:text-gray-400">8:20 PM ET</span>
                </div>

                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <div className="text-gray-600 dark:text-gray-400 mb-1">Spread</div>
                    <div className="font-medium text-gray-900 dark:text-white">DAL +3.5 | PHI -3.5</div>
                  </div>
                  <div>
                    <div className="text-gray-600 dark:text-gray-400 mb-1">Total</div>
                    <div className="font-medium text-gray-900 dark:text-white">O/U 42.5 (-105)</div>
                  </div>
                </div>
              </div>
            </>
          ) : (
            liveGames.map((game) => {
              const odds = mockOdds();
              return (
                <div key={game.id} className="border border-gray-200 dark:border-gray-700 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center space-x-2">
                      <span className="text-sm font-medium text-gray-900 dark:text-white">
                        {game.awayTeam} @ {game.homeTeam}
                      </span>
                      <Badge 
                        variant={game.status === "live" ? "destructive" : "secondary"} 
                        className="text-xs"
                      >
                        {game.status.toUpperCase()}
                      </Badge>
                    </div>
                    <span className="text-sm text-gray-500 dark:text-gray-400">
                      {formatGameTime(game.commenceTime, game.status)}
                    </span>
                  </div>

                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <div className="text-gray-600 dark:text-gray-400 mb-1">Moneyline</div>
                      <div className="font-medium text-gray-900 dark:text-white">
                        {game.homeTeam.slice(0, 3).toUpperCase()}: {odds.moneyline.home > 0 ? '+' : ''}{odds.moneyline.home} | 
                        {game.awayTeam.slice(0, 3).toUpperCase()}: {odds.moneyline.away > 0 ? '+' : ''}{odds.moneyline.away}
                      </div>
                    </div>
                    <div>
                      <div className="text-gray-600 dark:text-gray-400 mb-1">Total</div>
                      <div className="font-medium text-gray-900 dark:text-white">
                        O/U {odds.total.points} ({odds.total.odds})
                      </div>
                    </div>
                  </div>

                  <div className="mt-3 flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <span className="text-xs text-gray-500 dark:text-gray-400">Edge detected:</span>
                      <span className="text-xs font-medium text-success">+{odds.edge}% on Over</span>
                    </div>
                    <Button variant="link" size="sm" className="text-xs">
                      View Details
                    </Button>
                  </div>
                </div>
              );
            })
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/LoginButton.tsx.content:
import { Button } from "@/components/ui/button";
import { LogIn, LogOut, User } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";

export function LoginButton() {
  const { user, isLoading, isAuthenticated } = useAuth();

  if (isLoading) {
    return (
      <Button variant="outline" size="sm" disabled>
        <div className="w-4 h-4 animate-spin rounded-full border-2 border-primary border-t-transparent" />
      </Button>
    );
  }

  if (!isAuthenticated) {
    return (
      <Button 
        variant="outline"
        size="sm"
        onClick={() => {
          window.location.href = '/api/login';
        }}
        className="flex items-center gap-2 bg-white hover:bg-gray-50 border-gray-300 text-gray-700 dark:bg-gray-800 dark:hover:bg-gray-700 dark:border-gray-600 dark:text-gray-200"
      >
        <LogIn className="w-4 h-4" />
        Log in
      </Button>
    );
  }

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="outline" size="sm" className="flex items-center gap-2 bg-white hover:bg-gray-50 border-gray-300 text-gray-700 dark:bg-gray-800 dark:hover:bg-gray-700 dark:border-gray-600 dark:text-gray-200">
          <Avatar className="w-6 h-6">
            <AvatarImage src={user?.profileImageUrl} alt={user?.firstName || 'User'} />
            <AvatarFallback>
              {user?.firstName?.[0] || user?.email?.[0] || 'U'}
            </AvatarFallback>
          </Avatar>
          <span className="hidden sm:inline">
            {user?.firstName || user?.email || 'User'}
          </span>
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end">
        <DropdownMenuItem disabled>
          <User className="w-4 h-4 mr-2" />
          {user?.email}
        </DropdownMenuItem>
        <DropdownMenuItem onClick={() => window.location.href = '/api/logout'}>
          <LogOut className="w-4 h-4 mr-2" />
          Log out
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

client/src/components/MainDashboard.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Volleyball, Dumbbell, Beaker as Baseball, RefreshCw } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import RecommendationsCard from "./RecommendationsCard";
import LiveOddsMonitor from "./LiveOddsMonitor";
import ModelPerformance from "./ModelPerformance";
import { apiRequest } from "@/lib/queryClient";

interface MainDashboardProps {
  activeSport: string;
  onSportChange: (sport: string) => void;
}

interface ModelMetrics {
  accuracy: string;
  edgeDetectionRate: string;
  profitMargin: string;
  gamesAnalyzed: number;
  lastUpdate: string;
}

