          </CardContent>
        </Card>

        {/* Live Odds and Model Performance */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <LiveOddsMonitor liveGames={liveGames} />
          <ModelPerformance metrics={metrics} activeSport={activeSport} />
        </div>
      </div>
    </div>
  );
}

