                <Send className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/RecommendationsCar.content:
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

interface Recommendation {
  id: number;
  gameId: number;
  market: string;
  bet: string;
  edge: string;
  confidence: string;
  modelProbability: string;
  impliedProbability: string;
  bestOdds: string;
  bookmaker: string;
  status: string;
}

interface RecommendationsCardProps {
  recommendations: Recommendation[];
}

export default function RecommendationsCard({ recommendations }: RecommendationsCardProps) {
  const getConfidenceLevel = (confidence: string) => {
    const conf = parseFloat(confidence);
    if (conf >= 80) return { label: "HIGH CONFIDENCE", color: "bg-success" };
    if (conf >= 65) return { label: "MEDIUM CONFIDENCE", color: "bg-warning" };
    return { label: "LOW CONFIDENCE", color: "bg-gray-500" };
  };

  const getConfidenceColor = (confidence: string) => {
    const conf = parseFloat(confidence);
    if (conf >= 80) return "text-success";
    if (conf >= 65) return "text-warning";
    return "text-gray-500";
  };

  if (recommendations.length === 0) {
    return (
      <div className="text-center py-8">
        <div className="text-gray-500 dark:text-gray-400 mb-2">No recommendations available</div>
        <div className="text-sm text-gray-400 dark:text-gray-500">
          Check back later for AI-generated betting recommendations
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {recommendations.slice(0, 3).map((rec) => {
        const confidenceLevel = getConfidenceLevel(rec.confidence);
        const confidenceColor = getConfidenceColor(rec.confidence);
        const edge = parseFloat(rec.edge);

        return (
          <div
            key={rec.id}
            className={`flex items-center justify-between p-4 rounded-lg border ${
              edge >= 10
                ? "bg-green-50 dark:bg-green-900/20 border-green-200 dark:border-green-800"
                : edge >= 7
                ? "bg-yellow-50 dark:bg-yellow-900/20 border-yellow-200 dark:border-yellow-800"
                : "bg-blue-50 dark:bg-blue-900/20 border-blue-200 dark:border-blue-800"
            }`}
          >
            <div className="flex-1">
              <div className="flex items-center space-x-3 mb-2">
                <Badge className={`${confidenceLevel.color} text-white text-xs font-medium`}>
                  {confidenceLevel.label}
                </Badge>
                <span className="text-sm font-medium text-gray-900 dark:text-white">
                  Game #{rec.gameId}
                </span>
              </div>
              <div className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                {rec.bet} ({rec.market})
              </div>
              <div className="flex items-center space-x-4 text-sm">
                <span className={`font-medium ${
                  edge >= 10 ? "text-success" : edge >= 7 ? "text-warning" : "text-primary"
                }`}>
                  +{rec.edge}% Edge
                </span>
                <span className="text-gray-500 dark:text-gray-400">
                  Model: {rec.modelProbability}% | Implied: {rec.impliedProbability}%
                </span>
                <span className="text-gray-500 dark:text-gray-400">
                  Best Odds: {rec.bestOdds} ({rec.bookmaker})
                </span>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <div className="text-right">
                <div className={`text-lg font-bold ${confidenceColor}`}>
                  {rec.confidence}%
                </div>
                <div className="text-xs text-gray-500 dark:text-gray-400">Confidence</div>
              </div>
              <Button
                size="sm"
                className={`text-sm font-medium ${
                  edge >= 10
                    ? "bg-success hover:bg-green-600"
                    : edge >= 7
                    ? "bg-warning hover:bg-orange-600"
                    : "bg-primary hover:bg-blue-700"
                }`}
              >
                View Details
              </Button>
            </div>
          </div>
        );
      })}
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

