import { Express, Request, Response } from "express";
import { dailyPickService } from "./services/dailyPickService";
import { pickRotationService } from "./services/pickRotationService";
import { isAuthenticated } from "./replitAuth";

export function registerDailyPickRoutes(app: Express) {
  // Get today's pick of the day
  app.get("/api/daily-pick", async (req: Request, res: Response) => {
    try {
      const todaysPick = await dailyPickService.getTodaysPick();

      if (!todaysPick) {
        // Try to generate a new pick if none exists
        // First get today's games
        const gamesResponse = await fetch('http://localhost:5000/api/mlb/complete-schedule');
        const games = await gamesResponse.json();

        // Filter for upcoming games with odds (today or next few days)
        const today = new Date();
        const todaysGames = games.filter((game: any) => {
          const gameDate = new Date(game.commence_time);
          const daysDiff = Math.floor((gameDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
          return daysDiff >= 0 && daysDiff <= 3 && game.hasOdds; // Games within next 3 days
        });

        if (todaysGames.length > 0) {
          const newPick = await dailyPickService.generateAndSaveTodaysPick(todaysGames);
          return res.json(newPick);
        } else {
          return res.json(null);
        }
      }

      res.json(todaysPick);
    } catch (error) {
      console.error("Failed to get daily pick:", error);
      res.status(500).json({ error: "Failed to get daily pick" });
    }
  });

  // Get specific pitcher stats (development endpoint)
  app.get("/api/daily-pick/pitcher-stats/:name", async (req: Request, res: Response) => {
    try {
      const pitcherName = req.params.name;
      const stats = await dailyPickService.fetchReal2025PitcherStats(pitcherName);

      if (stats) {
        res.json({
          pitcher: pitcherName,
          stats,
          message: `Real 2025 season stats for ${pitcherName}`
        });
      } else {
        res.status(404).json({
          pitcher: pitcherName,
          error: "No 2025 stats found for this pitcher",
          message: "Pitcher may not be active or stats not available"
        });
      }
    } catch (error) {
      console.error("Failed to fetch pitcher stats:", error);
      res.status(500).json({ error: "Failed to fetch pitcher stats" });
    }
  });

  // Analyze any specific game for pick grading
  app.post("/api/daily-pick/analyze-game", async (req: Request, res: Response) => {
    try {
      const { gameId, homeTeam, awayTeam, pickTeam, odds, gameTime, venue } = req.body;

      if (!homeTeam || !awayTeam || !pickTeam || !odds) {
        return res.status(400).json({ error: "Missing required fields" });
      }

      // Generate analysis for this specific pick
      const analysis = await dailyPickService.generateGameAnalysis(
        homeTeam,
        awayTeam,
        pickTeam,
        odds,
        gameTime || new Date().toISOString(),
        venue || "TBD"
      );

      res.json(analysis);
    } catch (error) {
      console.error("Failed to analyze game:", error);
      res.status(500).json({ error: "Failed to analyze game" });
    }
  });

  // Test grading endpoint (development only)
  app.post("/api/daily-pick/test-new-grading", async (req: Request, res: Response) => {
    try {
      // Get today's games
      const gamesResponse = await fetch('http://localhost:5000/api/mlb/complete-schedule');
      const games = await gamesResponse.json();

      // Filter for upcoming games with odds
      const today = new Date();
      const todaysGames = games.filter((game: any) => {
        const gameDate = new Date(game.commence_time);
        const daysDiff = Math.floor((gameDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
        return daysDiff >= 0 && daysDiff <= 3 && game.hasOdds;
      });

      if (todaysGames.length === 0) {
        return res.status(400).json({ error: "No games with odds available for testing" });
      }

      // Generate new picks to test grading
      const newPick = await dailyPickService.generateDailyPick(todaysGames);
      const newLockPick = await dailyPickService.generateDailyPick(todaysGames);

      if (newPick && newLockPick) {
        // Save both picks for testing
        await dailyPickService.saveDailyPick(newPick);
        await dailyPickService.saveLockPick(newLockPick);

        res.json({ 
          dailyPick: newPick, 
          lockPick: newLockPick,
          message: "New picks generated with updated grading system",
          grades: {
            daily: newPick.grade,
            lock: newLockPick.grade
          }
        });
      } else {
        res.status(400).json({ error: "Could not generate suitable picks for testing" });
      }
    } catch (error) {
      console.error("Failed to test new grading:", error);
      res.status(500).json({ error: "Failed to test new grading system" });
    }
  });

  // Generate new daily pick (admin/testing endpoint)
  app.post("/api/daily-pick/generate", async (req: Request, res: Response) => {
    try {
      // Get today's games
      const gamesResponse = await fetch('http://localhost:5000/api/mlb/complete-schedule');
      const games = await gamesResponse.json();

      // Filter for upcoming games with odds (today or next few days)
      const today = new Date();
      const todaysGames = games.filter((game: any) => {
        const gameDate = new Date(game.commence_time);
        const daysDiff = Math.floor((gameDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
        return daysDiff >= 0 && daysDiff <= 3 && game.hasOdds; // Games within next 3 days
      });

      if (todaysGames.length === 0) {
        return res.status(400).json({ error: "No games with odds available for today" });
      }

      const newPick = await dailyPickService.generateDailyPick(todaysGames);

      if (newPick) {
        await dailyPickService.saveDailyPick(newPick);
        res.json(newPick);
      } else {
        res.status(400).json({ error: "Could not generate a suitable pick from available games" });
      }
    } catch (error) {
      console.error("Failed to generate daily pick:", error);
      res.status(500).json({ error: "Failed to generate daily pick" });
    }
  });

  // Get pick analysis breakdown (for info button)
  app.get("/api/daily-pick/:pickId/analysis", async (req: Request, res: Response) => {
    try {
      const { pickId } = req.params;

      // First try to find it as a daily pick
      let pick = await dailyPickService.getTodaysPick();

      // If not found or ID doesn't match, try lock pick
      if (!pick || pick.id !== pickId) {
        pick = await dailyPickService.getTodaysLockPick();
      }

      if (!pick || pick.id !== pickId) {
        return res.status(404).json({ error: "Pick not found" });
      }

      // Return detailed analysis breakdown
      const analysisBreakdown = {
        overall: {
          grade: pick.grade,
          confidence: pick.confidence,
          reasoning: pick.reasoning
        },
        // Frontend expects direct numerical properties on the analysis object
        offensiveProduction: pick.analysis.offensiveProduction || 75,
        pitchingMatchup: pick.analysis.pitchingMatchup || 75, 
        situationalEdge: pick.analysis.situationalEdge || 75,
        teamMomentum: pick.analysis.teamMomentum || 75,
        marketInefficiency: pick.analysis.marketInefficiency || 75,
        systemConfidence: pick.analysis.systemConfidence || 75,
        confidence: pick.analysis.confidence || 75,
        factors: {
          offensiveEdge: {
            score: pick.analysis.offensiveProduction,
            description: pick.analysis.offensiveProduction > 65 
              ? "Strong offensive edge with above-average contact quality"
              : pick.analysis.offensiveProduction < 45 
              ? "Below-average offensive production and contact quality"
              : "Average offensive capabilities"
          },
          pitchingMatchup: {
            score: pick.analysis.pitchingMatchup,
            description: pick.analysis.pitchingMatchup > 60
              ? "Favorable pitching matchup with recent form advantage"
              : pick.analysis.pitchingMatchup < 40
              ? "Challenging pitching matchup against quality starter"
              : "Neutral pitching matchup"
          },
          ballparkFactor: {
            score: pick.analysis.situationalEdge,
            description: pick.venue === 'Coors Field'
              ? "Coors Field environment favors teams that can handle offensive conditions"
              : pick.analysis.situationalEdge > 55
              ? "Hitter-friendly ballpark environment"
              : pick.analysis.situationalEdge < 45
              ? "Pitcher-friendly ballpark environment"
              : "Neutral ballpark environment"
          },
          weatherImpact: {
            score: pick.analysis.systemConfidence,
            description: "Weather conditions factored into analysis"
          },
          situationalEdge: {
            score: pick.analysis.teamMomentum,
            description: pick.analysis.teamMomentum > 60
              ? "Strong recent form and situational advantages"
              : pick.analysis.teamMomentum < 40
              ? "Recent struggles or situational disadvantages"
              : "Neutral recent form and situation"
          },
          valueScore: {
            score: pick.analysis.marketInefficiency,
            description: pick.analysis.marketInefficiency > 65
              ? "Excellent betting value - market appears to undervalue this team"
              : pick.analysis.marketInefficiency > 55
              ? "Good betting value identified"
              : "Fair market pricing"
          }
        },
        gameDetails: {
          matchup: `${pick.awayTeam} @ ${pick.homeTeam}`,
          venue: pick.venue,
          gameTime: pick.gameTime,
          pickTeam: pick.pickTeam,
          odds: pick.odds > 0 ? `+${pick.odds}` : `${pick.odds}`,
          probablePitchers: pick.probablePitchers
        }
      };

      res.json(analysisBreakdown);
    } catch (error) {
      console.error("Failed to get pick analysis:", error);
      res.status(500).json({ error: "Failed to get pick analysis" });
    }
  });

  // Get today's logged-in lock pick (for authenticated users)
  app.get("/api/daily-pick/lock", isAuthenticated, async (req: Request, res: Response) => {
    try {
      const todaysLockPick = await dailyPickService.getTodaysLockPick();

      if (!todaysLockPick) {
        // Try to generate a new lock pick if none exists
        const gamesResponse = await fetch('http://localhost:5000/api/mlb/complete-schedule');
        const games = await gamesResponse.json();

        // Filter for upcoming games with odds (today or next few days)
        const today = new Date();
        const todaysGames = games.filter((game: any) => {
          const gameDate = new Date(game.commence_time);
          const daysDiff = Math.floor((gameDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
          return daysDiff >= 0 && daysDiff <= 3 && game.hasOdds; // Games within next 3 days
        });

        if (todaysGames.length > 0) {
          const newLockPick = await dailyPickService.generateAndSaveTodaysLockPick(todaysGames);
          return res.json(newLockPick);
        } else {
          return res.json(null);
        }
      }

      res.json(todaysLockPick);
    } catch (error) {
      console.error("Failed to get lock pick:", error);
      res.status(500).json({ error: "Failed to get lock pick" });
    }
  });



  // Generate new lock pick (admin/testing endpoint)
  app.post("/api/daily-pick/lock/test-generate", async (req: Request, res: Response) => {
    try {
      console.log('üîß Lock pick test generation endpoint called');
      const gamesResponse = await fetch('http://localhost:5000/api/mlb/complete-schedule');
      const games = await gamesResponse.json();
      console.log(`üìä Retrieved ${games.length} games for lock pick generation`);

      const today = new Date();
      const todaysGames = games.filter((game: any) => {
        const gameDate = new Date(game.commence_time);
        const daysDiff = Math.floor((gameDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
        return daysDiff >= 0 && daysDiff <= 3 && game.hasOdds; // Games within next 3 days
      });

      if (todaysGames.length === 0) {
        return res.status(400).json({ error: "No games with odds available for today" });
      }

      const newLockPick = await dailyPickService.generateAndSaveTodaysLockPick(todaysGames);

      if (newLockPick) {
        res.json(newLockPick);
      } else {
        res.status(400).json({ error: "Could not generate a suitable lock pick from available games" });
      }
    } catch (error) {
      console.error("Failed to generate lock pick:", error);
      res.status(500).json({ error: "Failed to generate lock pick" });
    }
  });

  // Manual rotation endpoint for testing/admin use
  app.post("/api/daily-pick/rotate", async (req: Request, res: Response) => {
    try {
      console.log('üîß Manual pick rotation triggered via API');
      await pickRotationService.manualRotation();
      res.json({ 
        success: true, 
        message: "Pick rotation completed successfully",
        timestamp: new Date().toISOString()
      });
    } catch (error) {
      console.error("Failed to rotate picks:", error);
      res.status(500).json({ error: "Failed to rotate picks" });
    }
  });

  // Clear today's daily pick (testing/admin endpoint)
  app.delete("/api/daily-pick/clear", async (req: Request, res: Response) => {
    try {
      const today = new Date().toISOString().split('T')[0];
      console.log(`üßπ Clearing daily pick for ${today}`);

      // Try to delete from database
      try {
        const { db } = await import('./db');
        const { dailyPicks } = await import('../shared/schema');
        const { eq } = await import('drizzle-orm');

        const result = await db.delete(dailyPicks).where(eq(dailyPicks.pickDate, new Date(today)));
        console.log(`‚úÖ Deleted daily pick records from database`);
      } catch (dbError) {
        console.log('Database delete failed, clearing memory storage');
      }

      res.json({ 
        success: true, 
        message: "Daily pick cleared successfully",
        date: today,
        timestamp: new Date().toISOString()
      });
    } catch (error) {
      console.error("Failed to clear daily pick:", error);
      res.status(500).json({ error: "Failed to clear daily pick" });
    }
  });

  // Get all MLB picks for today (Pro users only)
  app.get("/api/daily-pick/all-picks", isAuthenticated, async (req: Request, res: Response) => {
    try {
      console.log('üìä Fetching all MLB picks for Pro user');

      // Get today's games
      const gamesResponse = await fetch('http://localhost:5000/api/mlb/complete-schedule');
      const games = await gamesResponse.json();

      // Filter for today's games with odds
      const today = new Date();
      const todaysGames = games.filter((game: any) => {
        const gameDate = new Date(game.commence_time);
        const daysDiff = Math.floor((gameDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
        return daysDiff >= 0 && daysDiff <= 1 && game.hasOdds; // Today's games
      });

      if (todaysGames.length === 0) {
        return res.json([]);
      }

      // Generate picks for all games
      const allPicks = await dailyPickService.generateAllGamePicks(todaysGames);

      console.log(`Generated ${allPicks.length} picks for Pro user view`);

      // Return all picks sorted by grade (best first)
      const sortedPicks = allPicks.sort((a, b) => {
        const getGradeValue = (grade: string): number => {
          const gradeMap: { [key: string]: number } = {
            'A+': 12, 'A': 11, 'A-': 10,
            'B+': 9, 'B': 8, 'B-': 7,
            'C+': 6, 'C': 5, 'C-': 4,
            'D+': 3, 'D': 2, 'F': 1
          };
          return gradeMap[grade] || 0;
        };

        return getGradeValue(b.grade) - getGradeValue(a.grade);
      });

      res.json(sortedPicks);
    } catch (error) {
      console.error("Failed to get all picks:", error);
      res.status(500).json({ error: "Failed to get all picks" });
    }
  });

  // Get rotation status endpoint
  app.get("/api/daily-pick/status", async (req: Request, res: Response) => {
    try {
      const dailyPick = await dailyPickService.getTodaysPick();
      const lockPick = await dailyPickService.getTodaysLockPick();

      let dailyPickStatus = 'none';
      let lockPickStatus = 'none';

      if (dailyPick) {
        const gameTime = new Date(dailyPick.gameTime);
        const now = new Date();
        if (now > gameTime) {
          dailyPickStatus = 'game_started';
        } else {
          dailyPickStatus = 'active';
        }
      }

      if (lockPick) {
        const gameTime = new Date(lockPick.gameTime);
        const now = new Date();
        if (now > gameTime) {
          lockPickStatus = 'game_started';
        } else {
          lockPickStatus = 'active';
        }
      }

      res.json({
        dailyPick: {
          status: dailyPickStatus,
          gameId: dailyPick?.gameId || null,
          gameTime: dailyPick?.gameTime || null,
          pickTeam: dailyPick?.pickTeam || null
        },
        lockPick: {
          status: lockPickStatus,
          gameId: lockPick?.gameId || null,
          gameTime: lockPick?.gameTime || null,
          pickTeam: lockPick?.pickTeam || null
        },
        nextRotationCheck: "Every 5 minutes",
        next2AMRotation: "2:00 AM EST daily"
      });
    } catch (error) {
      console.error("Failed to get rotation status:", error);
      res.status(500).json({ error: "Failed to get rotation status" });
    }
  });
}

server/routes-scores.ts.content:
import { Express, Request, Response } from "express";

interface MLBScore {
  gameId: number;
  gameDate: string;
  status: {
    abstractGameState: string;
    detailedState: string;
  };
  teams: {
    away: {
      team: {
        name: string;
      };
      score?: number;
    };
    home: {
      team: {
        name: string;
      };
      score?: number;
    };
  };
  linescore?: {
    currentInning?: number;
    inningState?: string;
  };
}

interface MLBScoresResponse {
  dates: Array<{
    date: string;
    games: MLBScore[];
  }>;
}

export function registerScoresRoutes(app: Express) {
  console.log('Registering scores routes...');

  // Get scores for specific date and sport
  app.get("/api/mlb/scores/:date", async (req: Request, res: Response) => {
    try {
      const { date } = req.params;

      console.log(`MLB scores route called for date: ${date}`);

      // Set JSON response header
      res.setHeader('Content-Type', 'application/json');

      console.log(`Fetching MLB scores for date: ${date}`);

      // Use MLB Stats API for scores
      const response = await fetch(
        `https://statsapi.mlb.com/api/v1/schedule?sportId=1&date=${date}&hydrate=team,linescore`
      );

      if (!response.ok) {
        throw new Error(`MLB API error: ${response.status}`);
      }

      const data: MLBScoresResponse = await response.json();

      const scores = data.dates.flatMap(date => 
        date.games.map((game, index) => ({
          id: `mlb_${game.gamePk || game.gameId || `${date.date}_${index}`}`,
          gameId: game.gamePk || game.gameId || `${date.date}_${index}`,
          homeTeam: game.teams.home.team.name,
          awayTeam: game.teams.away.team.name,
          homeScore: game.teams.home.score,
          awayScore: game.teams.away.score,
          status: game.status.detailedState,
          abstractGameState: game.status.abstractGameState,
          startTime: game.gameDate,
          inning: game.linescore?.currentInning ? `${game.linescore.currentInning}${game.linescore.inningState?.charAt(0) || ''}` : undefined,
          sportKey: 'baseball_mlb'
        }))
      );

      console.log(`Found ${scores.length} games for ${date}`);
      res.json(scores);
    } catch (error) {
      console.error(`Error fetching scores for ${req.params.date}:`, error);
      res.status(500).json({ error: "Failed to fetch scores" });
    }
  });

  // Get scores for different sports
  app.get("/api/scores/:sport", async (req: Request, res: Response) => {
    try {
      const { sport } = req.params;

      if (sport === 'baseball_mlb') {
        // Use MLB Stats API for scores
        const today = new Date().toISOString().split('T')[0];
        const startDate = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]; // Last 7 days

        const response = await fetch(
          `https://statsapi.mlb.com/api/v1/schedule?sportId=1&startDate=${startDate}&endDate=${today}&hydrate=team,linescore`
        );

        if (!response.ok) {
          throw new Error(`MLB API error: ${response.status}`);
        }

        const data: MLBScoresResponse = await response.json();

        const scores = data.dates.flatMap(date => 
          date.games.map(game => ({
            id: `mlb_${game.gameId}`,
            homeTeam: game.teams.home.team.name,
            awayTeam: game.teams.away.team.name,
            homeScore: game.teams.home.score,
            awayScore: game.teams.away.score,
            status: game.status.detailedState,
            startTime: game.gameDate,
            inning: game.linescore?.currentInning ? `${game.linescore.currentInning}${game.linescore.inningState?.charAt(0) || ''}` : undefined,
            sportKey: 'baseball_mlb'
          }))
        );

        res.json(scores);
      } else {
        // For other sports, return empty array for now
        // In the future, integrate with NFL/NBA APIs
        res.json([]);
      }
    } catch (error) {
      console.error(`Error fetching scores for ${req.params.sport}:`, error);
      res.status(500).json({ error: "Failed to fetch scores" });
    }
  });

  // Get live/recent scores summary
  app.get("/api/scores/summary", async (req: Request, res: Response) => {
    try {
      const today = new Date().toISOString().split('T')[0];

      // Get today's MLB games
      const response = await fetch(
        `https://statsapi.mlb.com/api/v1/schedule?sportId=1&date=${today}&hydrate=team,linescore`
      );

      if (!response.ok) {
        throw new Error(`MLB API error: ${response.status}`);
      }

      const data: MLBScoresResponse = await response.json();

      const summary = {
        baseball_mlb: {
          total: 0,
          live: 0,
          final: 0,
          scheduled: 0
        }
      };

      data.dates.forEach(date => {
        date.games.forEach(game => {
          summary.baseball_mlb.total++;
          const state = game.status.abstractGameState.toLowerCase();
          if (state === 'live') {
            summary.baseball_mlb.live++;
          } else if (state === 'final') {
            summary.baseball_mlb.final++;
          } else {
            summary.baseball_mlb.scheduled++;
          }
        });
      });

      res.json(summary);
    } catch (error) {
      console.error("Error fetching scores summary:", error);
      res.status(500).json({ error: "Failed to fetch scores summary" });
    }
  });
}

server/services/baseballSavantApi.ts.content:
import fetch from 'node-fetch';

export interface StatcastBatterStats {
  player_id: number;
  player_name: string;
  team_abbrev: string;
  avg_exit_velocity: number;
  max_exit_velocity: number;
  hard_hit_percent: number;
  barrel_percent: number;
  xwoba: number;
  xba: number;
  xslg: number;
  sweet_spot_percent: number;
  avg_launch_angle: number;
  chase_rate: number;
  whiff_rate: number;
  k_percent: number;
  bb_percent: number;
}

export interface StatcastPitcherStats {
  player_id: number;
  player_name: string;
  team_abbrev: string;
  avg_exit_velocity_against: number;
  hard_hit_percent_against: number;
  barrel_percent_against: number;
  xwoba_against: number;
  xera: number;
  avg_fastball_velocity: number;
  avg_spin_rate: number;
  k_percent: number;
  bb_percent: number;
  whiff_rate: number;
  chase_rate: number;
}

export interface TeamStatcastMetrics {
  team: string;
  batting_avg_exit_velocity: number;
  batting_hard_hit_percent: number;
  batting_barrel_percent: number;
  batting_xwoba: number;
  pitching_avg_exit_velocity_against: number;
  pitching_hard_hit_percent_against: number;
  pitching_barrel_percent_against: number;
  pitching_xwoba_against: number;
  runs_per_game: number;
  runs_allowed_per_game: number;
}

class BaseballSavantService {
  private readonly baseUrl = 'https://baseballsavant.mlb.com';

  /**
   * Fetch current season batter leaderboard with Statcast metrics
   */
  async getBatterStatcastStats(minPAs: number = 50): Promise<StatcastBatterStats[]> {
    try {
      const url = `${this.baseUrl}/leaderboard/statcast?type=batter&year=2025&position=&team=&min=${minPAs}&sort_col=xwoba&sort_order=desc`;
      console.log('Fetching Statcast batter stats from Baseball Savant...');

      // This would normally be a CSV download - we'll implement a parser
      const response = await fetch(url + '&csv=true');

      if (!response.ok) {
        throw new Error(`Failed to fetch Statcast batter data: ${response.statusText}`);
      }

      const csvData = await response.text();
      return this.parseBatterCSV(csvData);
    } catch (error) {
      console.error('Error fetching Statcast batter stats:', error);
      throw error;
    }
  }

  /**
   * Fetch current season pitcher leaderboard with Statcast metrics
   */
  async getPitcherStatcastStats(minBF: number = 50): Promise<StatcastPitcherStats[]> {
    try {
      const url = `${this.baseUrl}/leaderboard/statcast?type=pitcher&year=2025&position=&team=&min=${minBF}&sort_col=xera&sort_order=asc`;
      console.log('Fetching Statcast pitcher stats from Baseball Savant...');

      const response = await fetch(url + '&csv=true');

      if (!response.ok) {
        throw new Error(`Failed to fetch Statcast pitcher data: ${response.statusText}`);
      }

      const csvData = await response.text();
      return this.parsePitcherCSV(csvData);
    } catch (error) {
      console.error('Error fetching Statcast pitcher stats:', error);
      throw error;
    }
  }

  /**
   * Get team-level aggregated Statcast metrics
   */
  async getTeamStatcastMetrics(): Promise<TeamStatcastMetrics[]> {
    try {
      console.log('Calculating team-level Statcast metrics...');

      const [batters, pitchers] = await Promise.all([
        this.getBatterStatcastStats(25),
        this.getPitcherStatcastStats(25)
      ]);

      // Aggregate by team
      const teamMetrics: Map<string, TeamStatcastMetrics> = new Map();

      // Initialize teams
      const allTeams = [...new Set([...batters.map(b => b.team_abbrev), ...pitchers.map(p => p.team_abbrev)])];

      for (const team of allTeams) {
        const teamBatters = batters.filter(b => b.team_abbrev === team);
        const teamPitchers = pitchers.filter(p => p.team_abbrev === team);

        if (teamBatters.length === 0 || teamPitchers.length === 0) continue;

        teamMetrics.set(team, {
          team,
          batting_avg_exit_velocity: this.average(teamBatters.map(b => b.avg_exit_velocity)),
          batting_hard_hit_percent: this.average(teamBatters.map(b => b.hard_hit_percent)),
          batting_barrel_percent: this.average(teamBatters.map(b => b.barrel_percent)),
          batting_xwoba: this.average(teamBatters.map(b => b.xwoba)),
          pitching_avg_exit_velocity_against: this.average(teamPitchers.map(p => p.avg_exit_velocity_against)),
          pitching_hard_hit_percent_against: this.average(teamPitchers.map(p => p.hard_hit_percent_against)),
          pitching_barrel_percent_against: this.average(teamPitchers.map(p => p.barrel_percent_against)),
          pitching_xwoba_against: this.average(teamPitchers.map(p => p.xwoba_against)),
          runs_per_game: 0, // Will be filled from game data
          runs_allowed_per_game: 0 // Will be filled from game data
        });
      }

      return Array.from(teamMetrics.values());
    } catch (error) {
      console.error('Error calculating team Statcast metrics:', error);
      throw error;
    }
  }

  /**
   * Get player Statcast data for specific players (for probable pitchers)
   */
  async getPlayerStatcast(playerId: number, type: 'batter' | 'pitcher'): Promise<StatcastBatterStats | StatcastPitcherStats | null> {
    try {
      const stats = type === 'batter' 
        ? await this.getBatterStatcastStats(1)
        : await this.getPitcherStatcastStats(1);

      return stats.find(s => s.player_id === playerId) || null;
    } catch (error) {
      console.error(`Error fetching player ${playerId} Statcast data:`, error);
      return null;
    }
  }

  private parseBatterCSV(csvData: string): StatcastBatterStats[] {
    const lines = csvData.split('\n');
    const headers = lines[0].split(',');
    const data: StatcastBatterStats[] = [];

    for (let i = 1; i < lines.length; i++) {
      const values = lines[i].split(',');
      if (values.length < headers.length) continue;

      try {
        data.push({
          player_id: parseInt(values[0]) || 0,
          player_name: values[1] || '',
          team_abbrev: values[2] || '',
          avg_exit_velocity: parseFloat(values[3]) || 0,
          max_exit_velocity: parseFloat(values[4]) || 0,
          hard_hit_percent: parseFloat(values[5]) || 0,
          barrel_percent: parseFloat(values[6]) || 0,
          xwoba: parseFloat(values[7]) || 0,
          xba: parseFloat(values[8]) || 0,
          xslg: parseFloat(values[9]) || 0,
          sweet_spot_percent: parseFloat(values[10]) || 0,
          avg_launch_angle: parseFloat(values[11]) || 0,
          chase_rate: parseFloat(values[12]) || 0,
          whiff_rate: parseFloat(values[13]) || 0,
          k_percent: parseFloat(values[14]) || 0,
          bb_percent: parseFloat(values[15]) || 0,
        });
      } catch (error) {
        console.warn(`Error parsing batter row ${i}:`, error);
      }
    }

    return data;
  }

  private parsePitcherCSV(csvData: string): StatcastPitcherStats[] {
    const lines = csvData.split('\n');
    const headers = lines[0].split(',');
    const data: StatcastPitcherStats[] = [];

    for (let i = 1; i < lines.length; i++) {
      const values = lines[i].split(',');
      if (values.length < headers.length) continue;

      try {
        data.push({
          player_id: parseInt(values[0]) || 0,
          player_name: values[1] || '',
          team_abbrev: values[2] || '',
          avg_exit_velocity_against: parseFloat(values[3]) || 0,
          hard_hit_percent_against: parseFloat(values[4]) || 0,
          barrel_percent_against: parseFloat(values[5]) || 0,
          xwoba_against: parseFloat(values[6]) || 0,
          xera: parseFloat(values[7]) || 0,
          avg_fastball_velocity: parseFloat(values[8]) || 0,
          avg_spin_rate: parseFloat(values[9]) || 0,
          k_percent: parseFloat(values[10]) || 0,
          bb_percent: parseFloat(values[11]) || 0,
          whiff_rate: parseFloat(values[12]) || 0,
          chase_rate: parseFloat(values[13]) || 0,
        });
      } catch (error) {
        console.warn(`Error parsing pitcher row ${i}:`, error);
      }
    }

    return data;
  }

  private average(numbers: number[]): number {
    if (numbers.length === 0) return 0;
    return numbers.reduce((a, b) => a + b, 0) / numbers.length;
  }
}

export const baseballSavantService = new BaseballSavantService();

server/services/bettingRecommendationEng.content:
/**
 * Advanced Betting Recommendation Engine
 * Compares AI predictions with real market odds to identify value bets
 */

interface OddsData {
  homeMoneyline: number;
  awayMoneyline: number;
  homeSpread: number;
  awaySpread: number;
  spreadLine: number;
  overOdds: number;
  underOdds: number;
  totalLine: number;
}

interface PredictionData {
  homeWinProbability: number;
  awayWinProbability: number;
  homeSpreadProbability: number;
  awaySpreadProbability: number;
  overProbability: number;
  underProbability: number;
  predictedTotal: number;
  confidence: number;
}

interface BettingRecommendation {
  betType: 'moneyline'; // MONEYLINE ONLY - per user requirements
  selection: string;
  odds: number;
  impliedProbability: number;
  predictedProbability: number;
  edge: number;
  grade: 'A+' | 'A' | 'A-' | 'B+' | 'B' | 'B-' | 'C+' | 'C' | 'C-' | 'D+' | 'D' | 'F';
  confidence: number;
  reasoning: string;
  expectedValue: number;
  kellyBetSize: number;
}

export class BettingRecommendationEngine {

  /**
   * Convert American odds to decimal probability
   */
  private oddsToImpliedProbability(americanOdds: number): number {
    if (americanOdds > 0) {
      return 100 / (americanOdds + 100);
    } else {
      return Math.abs(americanOdds) / (Math.abs(americanOdds) + 100);
    }
  }

  /**
   * Calculate expected value of a bet (as ROI percentage)
   */
  private calculateExpectedValue(predictedProb: number, odds: number): number {
    const payoutMultiplier = odds > 0 ? (odds / 100) : (100 / Math.abs(odds));

    // EV = (Probability of Win √ó Profit) - (Probability of Loss √ó Loss)
    // This gives us expected profit per $1 wagered
    const expectedProfit = (predictedProb * payoutMultiplier) - ((1 - predictedProb) * 1);

    // Convert to ROI percentage (multiply by 100)
    return expectedProfit * 100;
  }

  /**
   * Calculate Kelly Criterion bet size
   */
  private calculateKellyBetSize(predictedProb: number, odds: number): number {
    const impliedProb = this.oddsToImpliedProbability(odds);
    const payoutMultiplier = odds > 0 ? (odds / 100) : (100 / Math.abs(odds));

    // Kelly = (bp - q) / b where b = odds, p = win prob, q = lose prob
    const kelly = ((payoutMultiplier * predictedProb) - (1 - predictedProb)) / payoutMultiplier;

    // Cap at 5% for safety (0.05 = 5% of bankroll)
    return Math.max(0, Math.min(kelly, 0.05));
  }

  /**
   * Assign letter grade based on edge and confidence - aligned with analysis factors scale
   */
  private assignGrade(edge: number, confidence: number): 'A+' | 'A' | 'A-' | 'B+' | 'B' | 'B-' | 'C+' | 'C' | 'C-' | 'D+' | 'D' | 'F' {
    // Convert edge and confidence to 60-100 scale to match analysis factors display
    const edgeScore = Math.min(100, 60 + (edge * 400)); // edge 0.1 = 100
    const confidenceScore = Math.min(100, 60 + (confidence * 40)); // confidence 1.0 = 100
    const avgScore = (edgeScore + confidenceScore) / 2;

    // Grade based on average score to match analysis factors logic
    if (avgScore >= 95) return 'A+';
    if (avgScore >= 90) return 'A'; 
    if (avgScore >= 85) return 'B+';
    if (avgScore >= 80) return 'B';
    if (avgScore >= 75) return 'C+';
    if (avgScore >= 70) return 'C';
    if (avgScore >= 65) return 'D+';
    if (avgScore >= 60) return 'D';
    return 'F';
  }

  /**
   * Extract odds from bookmakers array
   */
  private extractOddsFromBookmakers(bookmakers: any[]): OddsData | null {
    if (!bookmakers || bookmakers.length === 0) return null;

    // Use first available bookmaker
    const bookmaker = bookmakers[0];

    const h2hMarket = bookmaker.markets?.find((m: any) => m.key === 'h2h');
    const spreadsMarket = bookmaker.markets?.find((m: any) => m.key === 'spreads');
    const totalsMarket = bookmaker.markets?.find((m: any) => m.key === 'totals');

    if (!h2hMarket) return null;

    // For the betting recommendations, we need to match team names from the outcomes
    const outcomes = h2hMarket.outcomes;
    const homeOutcome = outcomes[0]; // First team in outcomes
    const awayOutcome = outcomes[1]; // Second team in outcomes

    const oddsData: OddsData = {
      homeMoneyline: homeOutcome?.price || 0,
      awayMoneyline: awayOutcome?.price || 0,
      homeSpread: 0,
      awaySpread: 0,
      spreadLine: 0,
      overOdds: 0,
      underOdds: 0,
      totalLine: 0
    };

    // Extract spread data
    if (spreadsMarket) {
      const spreadOutcomes = spreadsMarket.outcomes;
      oddsData.homeSpread = spreadOutcomes[0]?.price || 0;
      oddsData.awaySpread = spreadOutcomes[1]?.price || 0;
      oddsData.spreadLine = spreadOutcomes[0]?.point || 0;
    }

    // Extract totals data
    if (totalsMarket) {
      const overOutcome = totalsMarket.outcomes.find((o: any) => o.name === 'Over');
      const underOutcome = totalsMarket.outcomes.find((o: any) => o.name === 'Under');

      oddsData.overOdds = overOutcome?.price || 0;
      oddsData.underOdds = underOutcome?.price || 0;
      oddsData.totalLine = overOutcome?.point || 0;
    }

    console.log('Extracted odds data:', JSON.stringify(oddsData, null, 2));
    return oddsData;
  }

  /**
   * Generate comprehensive betting recommendations
   */
  public generateRecommendations(
    prediction: PredictionData,
    bookmakers: any[],
    homeTeam: string,
    awayTeam: string
  ): BettingRecommendation[] {
    console.log('üéØ Generating betting recommendations...');
    console.log('Prediction data:', JSON.stringify(prediction, null, 2));

    const odds = this.extractOddsFromBookmakers(bookmakers);
    if (!odds) {
      console.log('‚ùå No odds extracted from bookmakers');
      return [];
    }

    const recommendations: BettingRecommendation[] = [];

    // Moneyline Recommendations
    if (odds.homeMoneyline && odds.awayMoneyline) {
      // Home moneyline
      const homeEdge = prediction.homeWinProbability - this.oddsToImpliedProbability(odds.homeMoneyline);
      if (homeEdge > 0.01) { // Minimum 1% edge for quality picks
        recommendations.push({
          betType: 'moneyline',
          selection: `${homeTeam} ML`,
          odds: odds.homeMoneyline,
          impliedProbability: this.oddsToImpliedProbability(odds.homeMoneyline),
          predictedProbability: prediction.homeWinProbability,
          edge: homeEdge,
          grade: this.assignGrade(homeEdge, prediction.confidence),
          confidence: prediction.confidence,
          reasoning: `AI predicts ${homeTeam} wins ${(prediction.homeWinProbability * 100).toFixed(1)}% vs market ${(this.oddsToImpliedProbability(odds.homeMoneyline) * 100).toFixed(1)}%`,
          expectedValue: this.calculateExpectedValue(prediction.homeWinProbability, odds.homeMoneyline),
          kellyBetSize: this.calculateKellyBetSize(prediction.homeWinProbability, odds.homeMoneyline)
        });
      }

      // Away moneyline
      const awayEdge = prediction.awayWinProbability - this.oddsToImpliedProbability(odds.awayMoneyline);
      if (awayEdge > 0.01) { // Minimum 1% edge for quality picks
        recommendations.push({
          betType: 'moneyline',
          selection: `${awayTeam} ML`,
          odds: odds.awayMoneyline,
          impliedProbability: this.oddsToImpliedProbability(odds.awayMoneyline),
          predictedProbability: prediction.awayWinProbability,
          edge: awayEdge,
          grade: this.assignGrade(awayEdge, prediction.confidence),
          confidence: prediction.confidence,
          reasoning: `AI predicts ${awayTeam} wins ${(prediction.awayWinProbability * 100).toFixed(1)}% vs market ${(this.oddsToImpliedProbability(odds.awayMoneyline) * 100).toFixed(1)}%`,
          expectedValue: this.calculateExpectedValue(prediction.awayWinProbability, odds.awayMoneyline),
          kellyBetSize: this.calculateKellyBetSize(prediction.awayWinProbability, odds.awayMoneyline)
        });
      }
    }

    // REMOVED: Spread Recommendations - System configured for moneyline-only picks
    // Per user requirements: All daily picks must be moneyline bets only - no spread bets allowed

    // REMOVED: Total (Over/Under) Recommendations - System configured for moneyline-only picks
    // Per user requirements: All daily picks must be moneyline bets only - no over/under or spread bets allowed

    // Sort by grade and edge
    return recommendations.sort((a, b) => {
      const gradeOrder = ['A+', 'A', 'A-', 'B+', 'B', 'B-', 'C+', 'C', 'C-', 'D+', 'D', 'F'];
      const gradeComparison = gradeOrder.indexOf(a.grade) - gradeOrder.indexOf(b.grade);
      return gradeComparison !== 0 ? gradeComparison : b.edge - a.edge;
    });
  }
}

server/services/continuousTrainingServic.content:
import { db } from '../db';
import { 
  baseballTrainingData, 
  baseballGames,
  baseballModelTraining,
  InsertBaseballTrainingData,
  InsertBaseballModelTraining 
} from '@shared/schema';
import { eq, desc, and, sql } from 'drizzle-orm';

interface PredictionInputs {
  gameId: number;
  predictedHomeWin: number;
  predictedAwayWin: number;
  predictedTotal: number;
  predictedOverProb: number;
  predictedUnderProb: number;
  inputFeatures: any;
  marketData: {
    homeOdds: number;
    awayOdds: number;
    totalLine: number;
    overOdds: number;
    underOdds: number;
  };
  umpireData?: {
    name: string;
    strikeZoneAccuracy: number;
    consistencyRating: number;
    runsImpact: number;
  };
}

interface GameResult {
  homeScore: number;
  awayScore: number;
  gameComplete: boolean;
}

export class ContinuousTrainingService {

  /**
   * Store prediction data when a prediction is made
   */
  async storePrediction(prediction: PredictionInputs): Promise<number> {
    const trainingData: InsertBaseballTrainingData = {
      gameId: prediction.gameId,
      inputFeatures: prediction.inputFeatures,
      predictedHomeWin: prediction.predictedHomeWin,
      predictedAwayWin: prediction.predictedAwayWin,
      predictedTotal: prediction.predictedTotal,
      predictedOverProb: prediction.predictedOverProb,
      predictedUnderProb: prediction.predictedUnderProb,
      marketHomeOdds: prediction.marketData.homeOdds,
      marketAwayOdds: prediction.marketData.awayOdds,
      marketTotalLine: prediction.marketData.totalLine,
      marketOverOdds: prediction.marketData.overOdds,
      marketUnderOdds: prediction.marketData.underOdds,
      umpireName: prediction.umpireData?.name,
      umpireStrikeZoneAccuracy: prediction.umpireData?.strikeZoneAccuracy,
      umpireConsistencyRating: prediction.umpireData?.consistencyRating,
      umpireRunsImpact: prediction.umpireData?.runsImpact,
    };

    const [result] = await db.insert(baseballTrainingData).values(trainingData).returning({ id: baseballTrainingData.id });
    return result.id;
  }

  /**
   * Update training data with actual game results
   */
  async updateWithGameResult(gameId: number, result: GameResult): Promise<void> {
    if (!result.gameComplete) {
      return; // Don't update with partial results
    }

    const actualTotal = result.homeScore + result.awayScore;
    const actualHomeWin = result.homeScore > result.awayScore;

    // Get all training data entries for this game
    const trainingEntries = await db
      .select()
      .from(baseballTrainingData)
      .where(eq(baseballTrainingData.gameId, gameId));

    for (const entry of trainingEntries) {
      // Calculate actual over/under based on our predicted line
      const actualOver = entry.predictedTotal ? actualTotal > entry.predictedTotal : null;

      // Calculate accuracy metrics
      const homeWinAccuracy = entry.predictedHomeWin ? 
        Math.abs(entry.predictedHomeWin - (actualHomeWin ? 1 : 0)) : null;

      const totalAccuracy = entry.predictedTotal ? 
        Math.abs(entry.predictedTotal - actualTotal) : null;

      // Update the training data entry
      await db
        .update(baseballTrainingData)
        .set({
          actualHomeScore: result.homeScore,
          actualAwayScore: result.awayScore,
          actualTotal,
          actualHomeWin,
          actualOver,
          homeWinAccuracy,
          totalAccuracy,
          updatedAt: new Date(),
        })
        .where(eq(baseballTrainingData.id, entry.id));
    }
  }

  /**
   * Calculate model performance metrics
   */
  async calculateModelPerformance(
    dateFrom?: Date, 
    dateTo?: Date
  ): Promise<{
    totalGames: number;
    homeWinAccuracy: number;
    totalPredictionAccuracy: number;
    averageEdge: number;
    profitability: number;
    overUnderAccuracy: number;
  }> {
    let query = db
      .select({
        homeWinAccuracy: baseballTrainingData.homeWinAccuracy,
        totalAccuracy: baseballTrainingData.totalAccuracy,
        actualOver: baseballTrainingData.actualOver,
        predictedOverProb: baseballTrainingData.predictedOverProb,
        predictedUnderProb: baseballTrainingData.predictedUnderProb,
        marketOverOdds: baseballTrainingData.marketOverOdds,
        marketUnderOdds: baseballTrainingData.marketUnderOdds,
      })
      .from(baseballTrainingData)
      .where(
        and(
          sql`${baseballTrainingData.actualHomeScore} IS NOT NULL`,
          sql`${baseballTrainingData.actualAwayScore} IS NOT NULL`
        )
      );

    if (dateFrom) {
      query = query.where(sql`${baseballTrainingData.createdAt} >= ${dateFrom}`);
    }
    if (dateTo) {
      query = query.where(sql`${baseballTrainingData.createdAt} <= ${dateTo}`);
    }

    const results = await query;

    if (results.length === 0) {
      return {
        totalGames: 0,
        homeWinAccuracy: 0,
        totalPredictionAccuracy: 0,
        averageEdge: 0,
        profitability: 0,
        overUnderAccuracy: 0,
      };
    }

    // Calculate metrics
    const totalGames = results.length;

    // Home win accuracy (how close our win probability was)
    const validHomeWinAccuracies = results
      .filter(r => r.homeWinAccuracy !== null)
      .map(r => r.homeWinAccuracy!);
    const homeWinAccuracy = validHomeWinAccuracies.length > 0 
      ? 1 - (validHomeWinAccuracies.reduce((a, b) => a + b, 0) / validHomeWinAccuracies.length)
      : 0;

    // Total prediction accuracy (how close our total was)
    const validTotalAccuracies = results
      .filter(r => r.totalAccuracy !== null)
      .map(r => r.totalAccuracy!);
    const totalPredictionAccuracy = validTotalAccuracies.length > 0
      ? Math.max(0, 1 - (validTotalAccuracies.reduce((a, b) => a + b, 0) / validTotalAccuracies.length) / 10) // Normalize by 10 runs
      : 0;

    // Over/Under accuracy
    const overUnderPredictions = results.filter(r => 
      r.actualOver !== null && 
      r.predictedOverProb !== null && 
      r.predictedUnderProb !== null
    );

    let correctOverUnder = 0;
    for (const pred of overUnderPredictions) {
      const predictedOver = pred.predictedOverProb! > pred.predictedUnderProb!;
      if (predictedOver === pred.actualOver) {
        correctOverUnder++;
      }
    }

    const overUnderAccuracy = overUnderPredictions.length > 0 
      ? correctOverUnder / overUnderPredictions.length 
      : 0;

    // Calculate profitability (simplified simulation)
    let totalProfit = 0;
    let betsPlaced = 0;

    for (const result of overUnderPredictions) {
      const { predictedOverProb, predictedUnderProb, actualOver, marketOverOdds, marketUnderOdds } = result;

      if (!predictedOverProb || !predictedUnderProb || !marketOverOdds || !marketUnderOdds) continue;

      // Calculate implied probabilities from market odds
      const impliedOverProb = 1 / (marketOverOdds / 100 + 1);
      const impliedUnderProb = 1 / (marketUnderOdds / 100 + 1);

      // Check for edge (our probability vs market probability)
      const overEdge = predictedOverProb - impliedOverProb;
      const underEdge = predictedUnderProb - impliedUnderProb;

      // Place bet if we have significant edge (>5%)
      if (overEdge > 0.05) {
        betsPlaced++;
        if (actualOver) {
          totalProfit += marketOverOdds / 100; // Win
        } else {
          totalProfit -= 1; // Loss
        }
      } else if (underEdge > 0.05) {
        betsPlaced++;
        if (!actualOver) {
          totalProfit += marketUnderOdds / 100; // Win
        } else {
          totalProfit -= 1; // Loss
        }
      }
    }

    const profitability = betsPlaced > 0 ? totalProfit / betsPlaced : 0;
    const averageEdge = this.calculateAverageEdge(results);

    return {
      totalGames,
      homeWinAccuracy: Math.round(homeWinAccuracy * 10000) / 100, // Percentage
      totalPredictionAccuracy: Math.round(totalPredictionAccuracy * 10000) / 100,
      averageEdge: Math.round(averageEdge * 10000) / 100,
      profitability: Math.round(profitability * 10000) / 100,
      overUnderAccuracy: Math.round(overUnderAccuracy * 10000) / 100,
    };
  }

  /**
   * Identify areas where the model needs improvement
   */
  async identifyModelWeaknesses(): Promise<{
    weaknesses: string[];
    recommendations: string[];
    dataNeeds: string[];
  }> {
    const performance = await this.calculateModelPerformance();
    const weaknesses: string[] = [];
    const recommendations: string[] = [];
    const dataNeeds: string[] = [];

    // Analyze performance metrics
    if (performance.homeWinAccuracy < 60) {
      weaknesses.push('Low win probability accuracy');
      recommendations.push('Improve team strength modeling');
      dataNeeds.push('More detailed team form data');
    }

    if (performance.totalPredictionAccuracy < 70) {
      weaknesses.push('Poor total runs prediction');
      recommendations.push('Enhance scoring prediction algorithms');
      dataNeeds.push('Better offensive/defensive metrics');
    }

    if (performance.overUnderAccuracy < 55) {
      weaknesses.push('Over/Under predictions below random chance');
      recommendations.push('Recalibrate total prediction model');
      dataNeeds.push('Weather and ballpark factor improvements');
    }

    if (performance.profitability < 0) {
      weaknesses.push('Model losing money vs market odds');
      recommendations.push('Tighten edge detection criteria');
      dataNeeds.push('Real-time odds movement tracking');
    }

    if (performance.totalGames < 50) {
      weaknesses.push('Insufficient training data');
      recommendations.push('Collect more historical game data');
      dataNeeds.push('Expand data collection timeframe');
    }

    return { weaknesses, recommendations, dataNeeds };
  }

  /**
   * Store model training session results
   */
  async storeModelTrainingSession(
    modelVersion: string,
    trainingResults: {
      trainingDataSize: number;
      accuracy: number;
      precision: number;
      recall: number;
      f1Score: number;
      features: string[];
      hyperparameters: any;
    }
  ): Promise<void> {
    const trainingData: InsertBaseballModelTraining = {
      modelVersion,
      trainingDataSize: trainingResults.trainingDataSize,
      accuracy: trainingResults.accuracy,
      precision: trainingResults.precision,
      recall: trainingResults.recall,
      f1Score: trainingResults.f1Score,
      features: trainingResults.features,
      hyperparameters: JSON.stringify(trainingResults.hyperparameters),
    };

    await db.insert(baseballModelTraining).values(trainingData);
  }

  /**
   * Get model improvement trends over time
   */
  async getModelTrends(): Promise<{
    accuracy: Array<{ date: string; value: number }>;
    profitability: Array<{ date: string; value: number }>;
    totalGames: number;
  }> {
    // Get training sessions over time
    const trainingSessions = await db
      .select()
      .from(baseballModelTraining)
      .orderBy(desc(baseballModelTraining.trainedAt))
      .limit(10);

    const accuracy = trainingSessions.map(session => ({
      date: session.trainedAt?.toISOString().split('T')[0] || '',
      value: Math.round(session.accuracy * 100)
    }));

    // Calculate profitability trend by week
    const weeklyPerformance = await this.getWeeklyPerformance();

    return {
      accuracy,
      profitability: weeklyPerformance,
      totalGames: (await this.calculateModelPerformance()).totalGames
    };
  }

  /**
   * Calculate average edge from results
   */
  private calculateAverageEdge(results: any[]): number {
    let totalEdge = 0;
    let validEdges = 0;

    for (const result of results) {
      if (result.predictedOverProb && result.marketOverOdds) {
        const impliedProb = 1 / (result.marketOverOdds / 100 + 1);
        const edge = Math.abs(result.predictedOverProb - impliedProb);
        totalEdge += edge;
        validEdges++;
      }
    }

    return validEdges > 0 ? totalEdge / validEdges : 0;
  }

  /**
   * Get weekly performance trends
   */
  private async getWeeklyPerformance(): Promise<Array<{ date: string; value: number }>> {
    // This would calculate weekly profitability
    // For now, return mock data structure
    const weeks = [];
    const today = new Date();

    for (let i = 4; i >= 0; i--) {
      const weekStart = new Date(today);
      weekStart.setDate(today.getDate() - (i * 7));

      const weekEnd = new Date(weekStart);
      weekEnd.setDate(weekStart.getDate() + 6);

      const performance = await this.calculateModelPerformance(weekStart, weekEnd);

      weeks.push({
        date: weekStart.toISOString().split('T')[0],
        value: performance.profitability
      });
    }

    return weeks;
  }

  /**
   * Auto-retrain model when sufficient new data is available
   */
  async checkForAutoRetrain(): Promise<boolean> {
    // Get latest training session
    const [latestTraining] = await db
      .select()
      .from(baseballModelTraining)
      .orderBy(desc(baseballModelTraining.trainedAt))
      .limit(1);

    if (!latestTraining) {
      return true; // Need initial training
    }

    // Count new training data since last training session
    const newDataCount = await db
      .select({ count: sql<number>`count(*)` })
      .from(baseballTrainingData)
      .where(
        and(
          sql`${baseballTrainingData.actualHomeScore} IS NOT NULL`,
          sql`${baseballTrainingData.createdAt} > ${latestTraining.trainedAt}`
        )
      );

    const count = newDataCount[0]?.count || 0;

    // Retrain if we have 50+ new completed games
    return count >= 50;
  }
}

export const continuousTrainingService = new ContinuousTrainingService();

