import type { Express } from "express";
import { db } from "./db";
import { users, userFollows, userPicks } from "@shared/schema";
import { eq, and, or, ilike, ne, sql, desc, inArray } from "drizzle-orm";
import { isAuthenticated } from "./replitAuth";

export function registerFriendsRoutes(app: Express) {
  // Check username availability
  app.get('/api/users/check-username', isAuthenticated, async (req, res) => {
    try {
      const { username } = req.query;

      if (!username || typeof username !== 'string') {
        return res.status(400).json({ message: 'Username is required' });
      }

      // Check if username exists
      const existingUser = await db
        .select({ id: users.id })
        .from(users)
        .where(eq(users.username, username))
        .limit(1);

      res.json({ available: existingUser.length === 0 });
    } catch (error) {
      console.error('Error checking username:', error);
      res.status(500).json({ message: 'Failed to check username availability' });
    }
  });

  // Search for users
  app.get('/api/users/search', isAuthenticated, async (req, res) => {
    try {
      const currentUserId = req.user?.claims?.sub;
      const searchQuery = req.query.q as string;

      if (!searchQuery || searchQuery.length < 2) {
        return res.json([]);
      }

      // Search users by username, firstName, or lastName
      const searchResults = await db
        .select({
          id: users.id,
          username: users.username,
          firstName: users.firstName,
          lastName: users.lastName,
          profileImageUrl: users.profileImageUrl,
          bio: users.bio,
          followers: users.followers,
          following: users.following,
        })
        .from(users)
        .where(
          and(
            ne(users.id, currentUserId), // Exclude current user
            or(
              ilike(users.username, `%${searchQuery}%`),
              ilike(users.firstName, `%${searchQuery}%`),
              ilike(users.lastName, `%${searchQuery}%`)
            )
          )
        )
        .limit(10);

      res.json(searchResults);
    } catch (error) {
      console.error('Error searching users:', error);
      res.status(500).json({ message: 'Failed to search users' });
    }
  });

  // Follow a user
  app.post('/api/users/follow', isAuthenticated, async (req, res) => {
    try {
      const currentUserId = req.user?.claims?.sub;
      const { userId } = req.body;

      if (!userId || userId === currentUserId) {
        return res.status(400).json({ message: 'Invalid user ID' });
      }

      // Check if already following
      const existingFollow = await db
        .select()
        .from(userFollows)
        .where(
          and(
            eq(userFollows.followerId, currentUserId),
            eq(userFollows.followingId, userId)
          )
        );

      if (existingFollow.length > 0) {
        return res.status(400).json({ message: 'Already following this user' });
      }

      // Create follow relationship
      await db.insert(userFollows).values({
        followerId: currentUserId,
        followingId: userId,
      });

      // Update follower/following counts
      await db
        .update(users)
        .set({ following: sql`following + 1` })
        .where(eq(users.id, currentUserId));

      await db
        .update(users)
        .set({ followers: sql`followers + 1` })
        .where(eq(users.id, userId));

      res.json({ message: 'Successfully followed user' });
    } catch (error) {
      console.error('Error following user:', error);
      res.status(500).json({ message: 'Failed to follow user' });
    }
  });

  // Unfollow a user
  app.delete('/api/users/follow', isAuthenticated, async (req, res) => {
    try {
      const currentUserId = req.user?.claims?.sub;
      const { userId } = req.body;

      if (!userId || userId === currentUserId) {
        return res.status(400).json({ message: 'Invalid user ID' });
      }

      // Remove follow relationship
      const deleted = await db
        .delete(userFollows)
        .where(
          and(
            eq(userFollows.followerId, currentUserId),
            eq(userFollows.followingId, userId)
          )
        );

      if (deleted.count === 0) {
        return res.status(400).json({ message: 'Not following this user' });
      }

      // Update follower/following counts
      await db
        .update(users)
        .set({ following: sql`following - 1` })
        .where(eq(users.id, currentUserId));

      await db
        .update(users)
        .set({ followers: sql`followers - 1` })
        .where(eq(users.id, userId));

      res.json({ message: 'Successfully unfollowed user' });
    } catch (error) {
      console.error('Error unfollowing user:', error);
      res.status(500).json({ message: 'Failed to unfollow user' });
    }
  });

  // Get user's followers
  app.get('/api/users/:userId/followers', isAuthenticated, async (req, res) => {
    try {
      const { userId } = req.params;

      const followers = await db
        .select({
          id: users.id,
          username: users.username,
          firstName: users.firstName,
          lastName: users.lastName,
          profileImageUrl: users.profileImageUrl,
          bio: users.bio,
        })
        .from(userFollows)
        .innerJoin(users, eq(userFollows.followerId, users.id))
        .where(eq(userFollows.followingId, userId));

      res.json(followers);
    } catch (error) {
      console.error('Error fetching followers:', error);
      res.status(500).json({ message: 'Failed to fetch followers' });
    }
  });

  // Get users that a user is following
  app.get('/api/users/:userId/following', isAuthenticated, async (req, res) => {
    try {
      const { userId } = req.params;

      const following = await db
        .select({
          id: users.id,
          username: users.username,
          firstName: users.firstName,
          lastName: users.lastName,
          profileImageUrl: users.profileImageUrl,
          bio: users.bio,
        })
        .from(userFollows)
        .innerJoin(users, eq(userFollows.followingId, users.id))
        .where(eq(userFollows.followerId, userId));

      res.json(following);
    } catch (error) {
      console.error('Error fetching following:', error);
      res.status(500).json({ message: 'Failed to fetch following' });
    }
  });

  // Get social feed (picks from users you follow + your own picks)
  app.get('/api/users/feed', isAuthenticated, async (req, res) => {
    try {
      const currentUserId = req.user?.claims?.sub;
      const limit = parseInt(req.query.limit as string) || 20;
      const offset = parseInt(req.query.offset as string) || 0;

      // Get list of users the current user is following
      const followingUsers = await db
        .select({ userId: userFollows.followingId })
        .from(userFollows)
        .where(eq(userFollows.followerId, currentUserId));

      // Include followed users + current user
      const followingIds = followingUsers.map(f => f.userId);
      const allUserIds = [...followingIds, currentUserId];

      if (allUserIds.length === 0) {
        return res.json([]);
      }

      // Get picks from followed users + own picks (show by default unless isPublic is explicitly false)
      const feedPicks = await db
        .select({
          id: userPicks.id,
          userId: userPicks.userId,
          username: users.username,
          userAvatar: users.profileImageUrl,
          game: userPicks.game,
          selection: userPicks.selection,
          market: userPicks.market,
          line: userPicks.line,
          odds: userPicks.odds,
          units: userPicks.units,
          bookmakerDisplayName: userPicks.bookmakerDisplayName,
          status: userPicks.status,
          winAmount: userPicks.winAmount,
          createdAt: userPicks.createdAt,
          gameDate: userPicks.gameDate,
          gradedAt: userPicks.gradedAt,
        })
        .from(userPicks)
        .innerJoin(users, eq(userPicks.userId, users.id))
        .where(and(
          inArray(userPicks.userId, allUserIds),
          or(
            eq(userPicks.isPublic, true), // Show if explicitly set to true
            sql`${userPicks.isPublic} IS NULL` // Show if not set (default behavior - should appear in feed)
          )
        ))
        .orderBy(desc(userPicks.createdAt))
        .limit(limit)
        .offset(offset);

      res.json(feedPicks);
    } catch (error) {
      console.error('Error fetching social feed:', error);
      res.status(500).json({ message: 'Failed to fetch social feed' });
    }
  });
}

client/src/pages/user-profile.tsx.content:
import { useState, useEffect } from "react";
import { useParams } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Separator } from "@/components/ui/separator";
import { ActionStyleHeader } from "@/components/ActionStyleHeader";
import Footer from "@/components/Footer";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  User, 
  Users, 
  UserPlus, 
  UserMinus,
  TrendingUp, 
  TrendingDown, 
  Calendar,
  Target,
  Trophy,
  Clock,
  Lock,
  ArrowLeft,
  UserCheck,
  X
} from "lucide-react";

interface UserProfileData {
  id: string;
  username: string;
  profileImageUrl?: string;
  bio?: string;
  followers: number;
  following: number;
  createdAt: string;
  stats: {
    totalPicks?: number;
    pendingPicks?: number;
    winRate?: number;
    winStreak?: number;
  };
}

interface PublicFeedItem {
  id: string;
  type: 'pick' | 'win' | 'loss';
  pick: any;
  timestamp: string;
  result?: 'win' | 'loss' | 'push';
}

interface UserProfilePageProps {
  userId: string;
}

export default function UserProfilePage({ userId }: UserProfilePageProps) {
  const [darkMode, setDarkMode] = useState(true);
  const [isFollowing, setIsFollowing] = useState(false);
  const { user: currentUser } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Initialize dark mode from localStorage (default to dark mode)
  useEffect(() => {
    const savedDarkMode = localStorage.getItem('darkMode');
    const isDarkMode = savedDarkMode === null ? true : savedDarkMode === 'true';
    setDarkMode(isDarkMode);
    if (isDarkMode) {
      document.documentElement.classList.add('dark');
    }
    if (savedDarkMode === null) {
      localStorage.setItem('darkMode', 'true');
    }
  }, []);

  const toggleDarkMode = () => {
    const newDarkMode = !darkMode;
    setDarkMode(newDarkMode);
    document.documentElement.classList.toggle('dark', newDarkMode);
    localStorage.setItem('darkMode', newDarkMode.toString());
  };

  // Fetch user profile
  const { data: profileData, isLoading: profileLoading, error: profileError } = useQuery({
    queryKey: ['/api/profile', userId],
    enabled: !!userId,
  });

  // Fetch user's public picks feed
  const { data: publicFeed, isLoading: feedLoading } = useQuery({
    queryKey: ['/api/public-feed', userId],
    enabled: !!userId && !!profileData, // Only fetch if profile exists
  });

  // Check if current user is following this user
  const { data: followStatus } = useQuery({
    queryKey: ['/api/user/follow-status', userId],
    enabled: !!userId && !!currentUser,
  });

  useEffect(() => {
    if (followStatus) {
      setIsFollowing(followStatus.isFollowing);
    }
  }, [followStatus]);

  // Delete pick mutation
  const deletePick = useMutation({
    mutationFn: async (pickId: string) => {
      await apiRequest(`/api/user/picks/${pickId}`, {
        method: 'DELETE'
      });
    },
    onSuccess: () => {
      toast({
        title: "Pick deleted",
        description: "Your pick has been removed successfully.",
      });
      // Refresh the public feed
      queryClient.invalidateQueries({ queryKey: ['/api/public-feed', userId] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to delete pick. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleDeletePick = (pickId: string) => {
    if (confirm("Are you sure you want to delete this pick?")) {
      deletePick.mutate(pickId);
    }
  };

  const handleFollowToggle = async () => {
    if (!currentUser) {
      toast({
        title: "Authentication Required",
        description: "Please log in to follow users.",
        variant: "destructive",
      });
      return;
    }

    try {
      const endpoint = isFollowing ? '/api/user/unfollow' : '/api/user/follow';
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId }),
        credentials: 'include'
      });

      if (response.ok) {
        setIsFollowing(!isFollowing);
        toast({
          title: isFollowing ? "Unfollowed" : "Following",
          description: `You are now ${isFollowing ? 'not following' : 'following'} ${profileData?.username}`,
        });
      } else {
        throw new Error('Failed to update follow status');
      }
    } catch (error) {
      console.error('Error toggling follow:', error);
      toast({
        title: "Error",
        description: "Failed to update follow status. Please try again.",
        variant: "destructive",
      });
    }
  };

  const renderAvatar = (avatarString?: string, username?: string) => {
    if (avatarString?.includes('|')) {
      // New format: emoji|background
      const [emoji, backgroundClass] = avatarString.split('|');
      return (
        <div className={`w-24 h-24 rounded-full flex items-center justify-center border-4 border-gray-200 dark:border-gray-600 ${backgroundClass}`}>
          <span className="text-4xl">{emoji}</span>
        </div>
      );
    } else {
      return (
        <Avatar className="w-24 h-24">
          <AvatarImage src={avatarString} alt={username} />
          <AvatarFallback className="bg-blue-600 text-white text-2xl">
            {username?.charAt(0).toUpperCase() || 'U'}
          </AvatarFallback>
        </Avatar>
      );
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'long' 
    });
  };

  if (profileLoading) {
    return (
      <div className="min-h-screen bg-background">
        <ActionStyleHeader darkMode={darkMode} onToggleDarkMode={toggleDarkMode} />
        <div className="max-w-4xl mx-auto p-6">
          <div className="animate-pulse space-y-6">
            <div className="h-32 bg-gray-200 dark:bg-gray-700 rounded"></div>
            <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/3"></div>
            <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-2/3"></div>
          </div>
        </div>
      </div>
    );
  }

  if (profileError || !profileData) {
    return (
      <div className="min-h-screen bg-background">
        <ActionStyleHeader darkMode={darkMode} onToggleDarkMode={toggleDarkMode} />
        <div className="max-w-4xl mx-auto p-6">
          <Card>
            <CardContent className="p-8 text-center">
              <User className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                Profile Not Found
              </h3>
              <p className="text-gray-600 dark:text-gray-400 mb-4">
                This user's profile doesn't exist or is private.
              </p>
              <Button onClick={() => window.history.back()}>
                <ArrowLeft className="w-4 h-4 mr-2" />
                Go Back
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  const isOwnProfile = currentUser?.sub === userId;

  return (
    <div className="min-h-screen bg-background">
      <ActionStyleHeader darkMode={darkMode} onToggleDarkMode={toggleDarkMode} />

      <div className="max-w-4xl mx-auto p-6 space-y-6">
        {/* Back Button */}
        <Button variant="ghost" onClick={() => window.history.back()} className="mb-4">
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back
        </Button>

        {/* Profile Header */}
        <Card>
          <CardContent className="p-6">
            <div className="flex flex-col md:flex-row items-center md:items-start gap-6">
              {/* Avatar */}
              <div className="flex-shrink-0">
                {renderAvatar(profileData.profileImageUrl, profileData.username)}
              </div>

              {/* Profile Info */}
              <div className="flex-1 text-center md:text-left space-y-4">
                <div>
                  <h1 className="text-2xl font-bold text-gray-900 dark:text-white mb-1">
                    {profileData.username}
                  </h1>
                  <p className="text-gray-500 dark:text-gray-400 flex items-center justify-center md:justify-start gap-1">
                    <Calendar className="w-4 h-4" />
                    Joined {formatDate(profileData.createdAt)}
                  </p>
                </div>

                {profileData.bio && (
                  <p className="text-gray-700 dark:text-gray-300">
                    {profileData.bio}
                  </p>
                )}

                {/* Stats */}
                <div className="flex flex-wrap justify-center md:justify-start gap-4 text-sm">
                  <div className="flex items-center gap-1 text-gray-600 dark:text-gray-400">
                    <Users className="w-4 h-4" />
                    <span className="font-medium">{profileData.followers}</span> Followers
                  </div>
                  <div className="flex items-center gap-1 text-gray-600 dark:text-gray-400">
                    <UserCheck className="w-4 h-4" />
                    <span className="font-medium">{profileData.following}</span> Following
                  </div>
                </div>

                {/* Follow/Unfollow Button */}
                {!isOwnProfile && currentUser && (
                  <Button
                    onClick={handleFollowToggle}
                    variant={isFollowing ? "outline" : "default"}
                    className={isFollowing ? 
                      "border-red-500 text-red-500 hover:bg-red-50 dark:hover:bg-red-950" : 
                      "bg-blue-600 hover:bg-blue-700 text-white"
                    }
                  >
                    {isFollowing ? (
                      <>
                        <UserMinus className="w-4 h-4 mr-2" />
                        Unfollow
                      </>
                    ) : (
                      <>
                        <UserPlus className="w-4 h-4 mr-2" />
                        Follow
                      </>
                    )}
                  </Button>
                )}
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {profileData.stats.totalPicks !== null && (
            <Card>
              <CardContent className="p-4 text-center">
                <Target className="w-8 h-8 text-blue-600 mx-auto mb-2" />
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {profileData.stats.totalPicks || 0}
                </p>
                <p className="text-sm text-gray-500 dark:text-gray-400">Total Picks</p>
              </CardContent>
            </Card>
          )}

          {profileData.stats.pendingPicks !== null && (
            <Card>
              <CardContent className="p-4 text-center">
                <Clock className="w-8 h-8 text-yellow-600 mx-auto mb-2" />
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {profileData.stats.pendingPicks || 0}
                </p>
                <p className="text-sm text-gray-500 dark:text-gray-400">Pending</p>
              </CardContent>
            </Card>
          )}


        </div>

        {/* Public Picks Feed */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Target className="w-5 h-5" />
              Public Picks
            </CardTitle>
          </CardHeader>
          <CardContent>
            {feedLoading ? (
              <div className="space-y-4">
                {[...Array(3)].map((_, i) => (
                  <div key={i} className="p-4 border rounded-lg animate-pulse">
                    <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded mb-2"></div>
                    <div className="h-3 bg-gray-200 dark:bg-gray-700 rounded w-3/4"></div>
                  </div>
                ))}
              </div>
            ) : publicFeed && publicFeed.length > 0 ? (
              <div className="space-y-4">
                {publicFeed.map((item: PublicFeedItem) => (
                  <div key={item.id} className="p-4 border rounded-lg hover:shadow-sm transition-shadow">
                    <div className="flex items-center justify-between mb-2">
                      <Badge variant={
                        item.result === 'win' ? 'default' : 
                        item.result === 'loss' ? 'destructive' : 
                        'secondary'
                      }>
                        {item.result === 'win' ? 'Won' : 
                         item.result === 'loss' ? 'Lost' : 
                         'Pending'}
                      </Badge>
                      <div className="flex items-center gap-2">
                        <span className="text-sm text-gray-500 dark:text-gray-400">
                          {new Date(item.timestamp).toLocaleDateString()}
                        </span>
                        {isOwnProfile && (
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleDeletePick(item.id)}
                            className="text-red-500 border-red-500 hover:text-red-700 hover:bg-red-50 dark:hover:bg-red-900/20"
                            title="Delete this bet"
                          >
                            <X className="w-4 h-4 mr-1" />
                            Delete Bet
                          </Button>
                        )}
                      </div>
                    </div>
                    <p className="font-medium text-gray-900 dark:text-white">
                      {item.pick.selection}
                    </p>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      {item.pick.gameInfo?.game || `${item.pick.gameInfo?.awayTeam} @ ${item.pick.gameInfo?.homeTeam}` || 'Game info unavailable'}
                    </p>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-center text-gray-500 dark:text-gray-400 py-8">
                {isOwnProfile ? 
                  "You haven't made any public picks yet." :
                  "This user hasn't made any public picks yet."
                }
              </p>
            )}
          </CardContent>
        </Card>

        {/* Private Profile Message */}
        {Object.values(profileData.stats).every(stat => stat === null) && !isOwnProfile && (
          <Card>
            <CardContent className="p-8 text-center">
              <Lock className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                Private Profile
              </h3>
              <p className="text-gray-600 dark:text-gray-400">
                This user has made their stats private.
              </p>
            </CardContent>
          </Card>
        )}
      </div>

      <Footer />
    </div>
  );
}

