import { useState, useEffect } from 'react';
import { userPicksAPI } from '../services/userPicksApi';
import { Button } from '../components/ui/button';
import { Badge } from '../components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';

interface UserPickDisplay {
  id: number;
  gameInfo: {
    awayTeam: string;
    homeTeam: string;
    game: string;
    gameDate: string;
  };
  betInfo: {
    selection: string;
    market: string;
    line?: string;
    odds: number;
    units: number;
  };
  bookmaker: {
    key: string;
    displayName: string;
  };
  status: 'pending' | 'win' | 'loss' | 'push';
  result?: string;
  winAmount?: number;
  wagerAmount: number;
  potentialPayout: number;
  betUnitAtTime: number;
  createdAt: string;
}

export default function SimpleMyPicks() {
  const [picks, setPicks] = useState<UserPickDisplay[]>([]);
  const [loading, setLoading] = useState(true);
  const [editingOdds, setEditingOdds] = useState<number | null>(null);
  const [editingUnits, setEditingUnits] = useState<number | null>(null);
  const [tempOdds, setTempOdds] = useState('');
  const [tempUnits, setTempUnits] = useState('');

  useEffect(() => {
    loadPicks();
  }, []);

  const loadPicks = async () => {
    try {
      setLoading(true);
      const fetchedPicks = await userPicksAPI.getUserPicks();
      setPicks(fetchedPicks);
    } catch (error) {
      console.error('Error loading picks:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status: UserPickDisplay['status'], pick?: UserPickDisplay) => {
    const isToday = pick && new Date(pick.gameInfo.gameDate).toDateString() === new Date().toDateString();

    switch (status) {
      case 'pending':
        return (
          <div className="flex items-center gap-2">
            <Badge className={`${isToday ? 'bg-blue-600 animate-pulse' : 'bg-yellow-600'} text-white`}>
              {isToday ? 'Game Today' : 'Pending'}
            </Badge>
            {isToday && <span className="text-xs text-blue-600">üî¥ Check for live updates</span>}
          </div>
        );
      case 'win':
        return (
          <div className="flex items-center gap-2">
            <Badge className="bg-green-600 text-white">‚úì Won</Badge>
            {pick?.winAmount && (
              <span className="text-sm font-medium text-green-600">
                +{formatCurrency(pick.winAmount * (pick.betUnitAtTime || 50))}
              </span>
            )}
          </div>
        );
      case 'loss':
        return (
          <div className="flex items-center gap-2">
            <Badge className="bg-red-600 text-white">‚úó Lost</Badge>
            <span className="text-sm font-medium text-red-600">
              -{formatCurrency(pick?.wagerAmount || 50)}
            </span>
          </div>
        );
      case 'push':
        return <Badge className="bg-gray-600 text-white">‚Üî Push</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const formatOdds = (odds: number) => {
    return odds > 0 ? `+${odds}` : odds.toString();
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(amount);
  };

  const handleEditOdds = (pickId: number, currentOdds: number) => {
    setEditingOdds(pickId);
    setTempOdds(currentOdds.toString());
  };

  const handleSaveOdds = async (pickId: number) => {
    const odds = parseInt(tempOdds);
    if (isNaN(odds)) {
      alert('Please enter valid odds (e.g., -110, +150)');
      return;
    }

    try {
      const updatedPick = await userPicksAPI.updatePickOdds(pickId, odds);
      setPicks(picks.map(p => p.id === pickId ? updatedPick : p));
      setEditingOdds(null);
      setTempOdds('');
    } catch (error) {
      console.error('Error updating odds:', error);
      alert('Failed to update odds');
    }
  };

  const handleEditUnits = (pickId: number, currentUnits: number) => {
    setEditingUnits(pickId);
    setTempUnits(currentUnits.toString());
  };

  const handleSaveUnits = async (pickId: number) => {
    const units = parseFloat(tempUnits);
    if (isNaN(units) || units <= 0) {
      alert('Please enter valid units amount greater than 0');
      return;
    }

    try {
      const updatedPick = await userPicksAPI.updatePickUnits(pickId, units);
      setPicks(picks.map(p => p.id === pickId ? updatedPick : p));
      setEditingUnits(null);
      setTempUnits('');
    } catch (error) {
      console.error('Error updating units:', error);
      alert('Failed to update units');
    }
  };

  const handleCancel = () => {
    setEditingOdds(null);
    setEditingUnits(null);
    setTempOdds('');
    setTempUnits('');
  };

  const handleDeletePick = async (pickId: number) => {
    if (!confirm('Are you sure you want to delete this pick? This action cannot be undone.')) {
      return;
    }

    try {
      await userPicksAPI.deletePick(pickId);
      setPicks(picks.filter(p => p.id !== pickId));
    } catch (error) {
      console.error('Error deleting pick:', error);
      alert('Failed to delete pick');
    }
  };

  if (loading) {
    return <div className="p-6">Loading picks...</div>;
  }

  const pendingPicks = picks.filter(p => p.status === 'pending');
  const pastPicks = picks.filter(p => p.status !== 'pending');

  const totalWon = pastPicks.filter(p => p.status === 'win').reduce((sum, p) => sum + (p.winAmount || 0), 0);
  const totalLost = pastPicks.filter(p => p.status === 'loss').reduce((sum, p) => sum + p.wagerAmount, 0);
  const netProfit = totalWon - totalLost;

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-6">
        <h1 className="text-2xl font-bold mb-4">My Picks</h1>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <Card>
            <CardContent className="p-4">
              <div className="text-2xl font-bold text-blue-600">{picks.length}</div>
              <div className="text-sm text-gray-600">Total Picks</div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4">
              <div className="text-2xl font-bold text-yellow-600">{pendingPicks.length}</div>
              <div className="text-sm text-gray-600">Pending</div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4">
              <div className="text-2xl font-bold text-green-600">
                {pastPicks.filter(p => p.status === 'win').length}
              </div>
              <div className="text-sm text-gray-600">Won</div>
            </CardContent>
          </Card>
          <Card>
            <CardContent className="p-4">
              <div className={`text-2xl font-bold ${netProfit >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                {formatCurrency(netProfit)}
              </div>
              <div className="text-sm text-gray-600">Net Profit</div>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Pending Picks */}
      {pendingPicks.length > 0 && (
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-4 text-yellow-600">Pending Picks</h2>
          <div className="space-y-4">
            {pendingPicks.map(pick => (
              <Card key={pick.id}>
                <CardContent className="p-4">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <div className="font-semibold text-lg">{pick.gameInfo.game}</div>
                      <div className="text-gray-600 mb-2">
                        {pick.betInfo.selection} {pick.betInfo.market} 
                        {pick.betInfo.line && ` ${pick.betInfo.line}`}
                      </div>
                      <div className="text-sm text-gray-500">
                        {pick.bookmaker.displayName} ‚Ä¢ {new Date(pick.gameInfo.gameDate).toLocaleDateString()}
                      </div>
                    </div>
                    <div className="text-right ml-4">
                      {getStatusBadge(pick.status)}
                      <div className="mt-2 text-sm">
                        <div>
                          {editingOdds === pick.id ? (
                            <div className="flex items-center space-x-2">
                              <input
                                type="number"
                                value={tempOdds}
                                onChange={(e) => setTempOdds(e.target.value)}
                                className="w-20 px-2 py-1 border rounded"
                                placeholder="Odds"
                              />
                              <Button size="sm" onClick={() => handleSaveOdds(pick.id)}>Save</Button>
                              <Button size="sm" variant="outline" onClick={handleCancel}>Cancel</Button>
                            </div>
                          ) : (
                            <div className="flex items-center space-x-2">
                              <span>Odds: {formatOdds(pick.betInfo.odds)}</span>
                              {pick.status === 'pending' && (
                                <Button 
                                  size="sm" 
                                  variant="outline"
                                  onClick={() => handleEditOdds(pick.id, pick.betInfo.odds)}
                                >
                                  Edit
                                </Button>
                              )}
                            </div>
                          )}
                        </div>
                        <div className="mt-1">
                          {editingUnits === pick.id ? (
                            <div className="flex items-center space-x-2">
                              <input
                                type="number"
                                step="0.5"
                                value={tempUnits}
                                onChange={(e) => setTempUnits(e.target.value)}
                                className="w-20 px-2 py-1 border rounded"
                                placeholder="Units"
                              />
                              <Button size="sm" onClick={() => handleSaveUnits(pick.id)}>Save</Button>
                              <Button size="sm" variant="outline" onClick={handleCancel}>Cancel</Button>
                            </div>
                          ) : (
                            <div className="flex items-center space-x-2">
                              <span>{pick.betInfo.units} units ({formatCurrency(pick.wagerAmount)})</span>
                              {pick.status === 'pending' && (
                                <Button 
                                  size="sm" 
                                  variant="outline"
                                  onClick={() => handleEditUnits(pick.id, pick.betInfo.units)}
                                >
                                  Edit
                                </Button>
                              )}
                            </div>
                          )}
                        </div>
                        <div className="mt-1">
                          Potential: {formatCurrency(pick.potentialPayout)}
                        </div>
                        <div className="mt-2">
                          <Button 
                            size="sm" 
                            variant="destructive"
                            onClick={() => handleDeletePick(pick.id)}
                          >
                            Delete
                          </Button>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* Past Picks */}
      {pastPicks.length > 0 && (
        <div>
          <h2 className="text-xl font-semibold mb-4">Past Picks</h2>
          <div className="space-y-4">
            {pastPicks.map(pick => (
              <Card key={pick.id}>
                <CardContent className="p-4">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <div className="font-semibold text-lg">{pick.gameInfo.game}</div>
                      <div className="text-gray-600 mb-2">
                        {pick.betInfo.selection} {pick.betInfo.market}
                        {pick.betInfo.line && ` ${pick.betInfo.line}`}
                      </div>
                      <div className="text-sm text-gray-500">
                        {pick.bookmaker.displayName} ‚Ä¢ {new Date(pick.gameInfo.gameDate).toLocaleDateString()}
                      </div>
                      {pick.result && (
                        <div className="text-sm text-gray-600 mt-1">{pick.result}</div>
                      )}
                    </div>
                    <div className="text-right ml-4">
                      {getStatusBadge(pick.status)}
                      <div className="mt-2 text-sm">
                        <div>Odds: {formatOdds(pick.betInfo.odds)}</div>
                        <div>{pick.betInfo.units} units ({formatCurrency(pick.wagerAmount)})</div>
                        <div className={pick.status === 'win' ? 'text-green-600 font-semibold' : 
                                       pick.status === 'loss' ? 'text-red-600 font-semibold' : ''}>
                          {pick.status === 'win' ? `+${formatCurrency(pick.winAmount || 0)}` :
                           pick.status === 'loss' ? `-${formatCurrency(pick.wagerAmount)}` :
                           'Push'}
                        </div>
                        <div className="mt-2">
                          <Button 
                            size="sm" 
                            variant="destructive"
                            onClick={() => handleDeletePick(pick.id)}
                          >
                            Delete
                          </Button>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {picks.length === 0 && (
        <div className="text-center py-12">
          <div className="text-gray-500 text-lg">No picks yet</div>
          <div className="text-gray-400 mt-2">Start making your first bet to see it here!</div>
        </div>
      )}
    </div>
  );
}

client/src/services/userPicksApi.ts.content:
interface UserPickFromDB {
  id: number;
  user_id: string;
  game_id: string;
  home_team: string;
  away_team: string;
  selection: string;
  game: string;
  market: string;
  line: string | null;
  odds: number;
  units: number;
  bookmaker: string;
  bookmaker_display_name: string;
  status: 'pending' | 'win' | 'loss' | 'push';
  result: string | null;
  win_amount: number | null;
  game_date: string;
  created_at: string;
  bet_unit_at_time: number;
}

interface UserPickDisplay {
  id: number;
  gameInfo: {
    awayTeam: string;
    homeTeam: string;
    game: string;
    gameDate: string;
  };
  betInfo: {
    selection: string;
    market: string;
    line?: string;
    odds: number;
    units: number;
  };
  bookmaker: {
    key: string;
    displayName: string;
  };
  status: 'pending' | 'win' | 'loss' | 'push';
  result?: string;
  winAmount?: number;
  wagerAmount: number;
  potentialPayout: number;
  betUnitAtTime: number;
  createdAt: string;
}

export class UserPicksAPI {
  private baseURL = '/api/user/picks';

  async getUserPicks(): Promise<UserPickDisplay[]> {
    const response = await fetch(this.baseURL, {
      credentials: 'include'
    });

    if (!response.ok) {
      throw new Error(`Failed to fetch picks: ${response.status}`);
    }

    const picks: UserPickFromDB[] = await response.json();
    return picks.map(this.transformPick);
  }

  async updatePickOdds(pickId: number, odds: number): Promise<UserPickDisplay> {
    const response = await fetch(`${this.baseURL}/${pickId}/odds`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify({ odds })
    });

    if (!response.ok) {
      throw new Error(`Failed to update odds: ${response.status}`);
    }

    const pick: UserPickFromDB = await response.json();
    return this.transformPick(pick);
  }

  async updatePickUnits(pickId: number, units: number): Promise<UserPickDisplay> {
    const response = await fetch(`${this.baseURL}/${pickId}/units`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify({ units })
    });

    if (!response.ok) {
      throw new Error(`Failed to update units: ${response.status}`);
    }

    const pick: UserPickFromDB = await response.json();
    return this.transformPick(pick);
  }

  async deletePick(pickId: number): Promise<void> {
    const response = await fetch(`${this.baseURL}/${pickId}`, {
      method: 'DELETE',
      credentials: 'include'
    });

    if (!response.ok) {
      throw new Error(`Failed to delete pick: ${response.status}`);
    }
  }

  private transformPick(pick: UserPickFromDB): UserPickDisplay {
    const wagerAmount = pick.units * pick.bet_unit_at_time;
    const potentialPayout = this.calculatePayout(wagerAmount, pick.odds);

    return {
      id: pick.id,
      gameInfo: {
        awayTeam: pick.away_team,
        homeTeam: pick.home_team,
        game: pick.game,
        gameDate: pick.game_date
      },
      betInfo: {
        selection: pick.selection,
        market: pick.market,
        line: pick.line || undefined,
        odds: pick.odds,
        units: pick.units
      },
      bookmaker: {
        key: pick.bookmaker,
        displayName: pick.bookmaker_display_name
      },
      status: pick.status,
      result: pick.result || undefined,
      winAmount: pick.win_amount || undefined,
      wagerAmount,
      potentialPayout,
      betUnitAtTime: pick.bet_unit_at_time,
      createdAt: pick.created_at
    };
  }

  private calculatePayout(wager: number, odds: number): number {
    if (odds === 0) return wager;

    if (odds > 0) {
      // Positive odds: payout = wager + (wager * (odds/100))
      return wager + (wager * (odds / 100));
    } else {
      // Negative odds: payout = wager + (wager * (100/Math.abs(odds)))
      return wager + (wager * (100 / Math.abs(odds)));
    }
  }
}

export const userPicksAPI = new UserPicksAPI();

client/src/pages/my-picks-fixed.tsx.content:
import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import ActionStyleHeader from '@/components/ActionStyleHeader';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { 
  Target, 
  ExternalLink, 
  Trash2, 
  Calendar,
  DollarSign,
  BarChart3,
  AlertCircle,
  Edit3,
  Save,
  X,
  Plus
} from "lucide-react";
import { useAuth } from '@/hooks/useAuth';

export default function MyPicksPageFixed() {
  const [darkMode, setDarkMode] = useState(true);
  const { user, isAuthenticated, isLoading: authLoading } = useAuth();
  const [selectedStatus, setSelectedStatus] = useState<'all' | 'pending' | 'past'>('all');
  const [editingOdds, setEditingOdds] = useState<string | null>(null);
  const [tempOdds, setTempOdds] = useState<string>('');

  // Initialize dark mode from localStorage (default to dark mode)
  useEffect(() => {
    const savedDarkMode = localStorage.getItem('darkMode');
    const isDarkMode = savedDarkMode === null ? true : savedDarkMode === 'true';
    setDarkMode(isDarkMode);
    if (isDarkMode) {
      document.documentElement.classList.add('dark');
    }
    if (savedDarkMode === null) {
      localStorage.setItem('darkMode', 'true');
    }
  }, []);

  const toggleDarkMode = () => {
    const newDarkMode = !darkMode;
    setDarkMode(newDarkMode);
    document.documentElement.classList.toggle('dark', newDarkMode);
    localStorage.setItem('darkMode', newDarkMode.toString());
  };

  // Use database-only approach with TanStack Query
  const { data: userPicks = [], isLoading, refetch } = useQuery({
    queryKey: ['/api/user/picks'],
    enabled: isAuthenticated
  });

  // Authentication guard
  if (!isAuthenticated && !authLoading) {
    return (
      <div className="min-h-screen bg-background">
        <ActionStyleHeader darkMode={darkMode} onToggleDarkMode={toggleDarkMode} />
        <div className="max-w-4xl mx-auto p-6">
          <Card className="bg-white dark:bg-gray-800">
            <CardContent className="p-6 text-center">
              <Target className="w-16 h-16 mx-auto mb-4 text-gray-400" />
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                Log in to View Data
              </h3>
              <p className="text-gray-600 dark:text-gray-400 mb-4">
                Sign in to track your betting picks and performance
              </p>
              <Button onClick={() => window.location.href = '/api/login'}>
                Log in
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  // Show loading state
  if (authLoading || isLoading) {
    return (
      <div className="min-h-screen bg-background">
        <ActionStyleHeader darkMode={darkMode} onToggleDarkMode={toggleDarkMode} />
        <div className="max-w-7xl mx-auto px-3 sm:px-4 md:px-6 py-4 space-y-4 sm:space-y-6 pb-20 sm:pb-6">
          <div className="space-y-4">
            {[...Array(3)].map((_, i) => (
              <Card key={i} className="bg-white dark:bg-gray-800">
                <CardContent className="p-4">
                  <div className="animate-pulse">
                    <div className="flex items-start gap-3">
                      <div className="w-12 h-12 bg-gray-200 dark:bg-gray-700 rounded-lg"></div>
                      <div className="flex-1 space-y-2">
                        <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-3/4"></div>
                        <div className="h-3 bg-gray-200 dark:bg-gray-700 rounded w-1/2"></div>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      </div>
    );
  }

  // Filter picks safely
  const picksArray = Array.isArray(userPicks) ? userPicks : [];
  const filteredPicks = picksArray.filter((pick: any) => {
    if (!pick || typeof pick !== 'object') return false;
    if (selectedStatus === 'all') return true;
    if (selectedStatus === 'past') return ['won', 'lost', 'win', 'loss', 'push'].includes(pick.status);
    return pick.status === selectedStatus;
  });

  // Calculate stats safely
  const stats = {
    total: picksArray.length,
    pending: picksArray.filter((p: any) => p?.status === 'pending').length,
    won: picksArray.filter((p: any) => ['won', 'win'].includes(p?.status)).length,
    lost: picksArray.filter((p: any) => ['lost', 'loss'].includes(p?.status)).length,
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge className="bg-yellow-600 text-white">Pending</Badge>;
      case 'won':
      case 'win':
        return <Badge className="bg-green-600 text-white">Won</Badge>;
      case 'lost':
      case 'loss':
        return <Badge className="bg-red-600 text-white">Lost</Badge>;
      case 'push':
        return <Badge className="bg-gray-600 text-white">Push</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const formatOdds = (odds: number) => {
    return odds > 0 ? `+${odds}` : odds.toString();
  };

  const formatBet = (pick: any) => {
    if (!pick) return 'Unknown';

    const odds = pick.odds ? formatOdds(pick.odds) : '';
    const market = pick.market || 'unknown';
    const selection = pick.selection || 'Unknown';
    const line = pick.line;

    if (market === 'moneyline') {
      return `${selection} ML ${odds}`;
    }
    if (market === 'spread') {
      const lineValue = line || 0;
      return `${selection} ${lineValue > 0 ? '+' : ''}${lineValue} ${odds}`;
    }
    if (market === 'total') {
      return `${selection} ${line || ''} ${odds}`;
    }
    return `${selection} ${market} ${odds}`;
  };

  const deletePick = async (pickId: string) => {
    if (confirm('Are you sure you want to delete this pick?')) {
      try {
        await apiRequest('DELETE', `/api/user/picks/${pickId}`);
        refetch();
      } catch (error) {
        console.error('Error deleting pick:', error);
        alert('Failed to delete pick. Please try again.');
      }
    }
  };

  const handleEditOdds = (pickId: string, currentOdds: number) => {
    setEditingOdds(pickId);
    setTempOdds(currentOdds === 0 ? '' : currentOdds.toString());
  };

  const handleSaveOdds = async (pickId: string) => {
    const odds = parseFloat(tempOdds);
    if (isNaN(odds) || odds === 0) {
      alert('Please enter valid odds (e.g., -110, +150)');
      return;
    }

    try {
      await apiRequest('PATCH', `/api/user/picks/${pickId}/odds`, { odds });
      refetch();
      setEditingOdds(null);
      setTempOdds('');
    } catch (error) {
      console.error('Error updating odds:', error);
      alert('Failed to update odds. Please try again.');
    }
  };

  const handleCancelEdit = () => {
    setEditingOdds(null);
    setTempOdds('');
  };

  return (
    <div className="min-h-screen bg-background">
      <ActionStyleHeader darkMode={darkMode} onToggleDarkMode={toggleDarkMode} />

      <div className="max-w-7xl mx-auto px-3 sm:px-4 md:px-6 py-4 space-y-4 sm:space-y-6 pb-20 sm:pb-6">
        {/* Stats Cards */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <Card className="bg-white dark:bg-gray-800">
            <CardContent className="p-4">
              <div className="flex items-center space-x-2">
                <Target className="w-5 h-5 text-blue-500" />
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-300">Total Picks</p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">{stats.total}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="bg-white dark:bg-gray-800">
            <CardContent className="p-4">
              <div className="flex items-center space-x-2">
                <Calendar className="w-5 h-5 text-yellow-500" />
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-300">Pending</p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">{stats.pending}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="bg-white dark:bg-gray-800">
            <CardContent className="p-4">
              <div className="flex items-center space-x-2">
                <BarChart3 className="w-5 h-5 text-green-500" />
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-300">Won</p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">{stats.won}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="bg-white dark:bg-gray-800">
            <CardContent className="p-4">
              <div className="flex items-center space-x-2">
                <AlertCircle className="w-5 h-5 text-red-500" />
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-300">Lost</p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">{stats.lost}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Filter Tabs */}
        <Tabs value={selectedStatus} onValueChange={(value) => setSelectedStatus(value as any)}>
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="all">All ({stats.total})</TabsTrigger>
            <TabsTrigger value="pending">Pending ({stats.pending})</TabsTrigger>
            <TabsTrigger value="past">Past ({stats.won + stats.lost})</TabsTrigger>
          </TabsList>

          <TabsContent value={selectedStatus} className="space-y-4 mt-4">
            {filteredPicks.length === 0 ? (
              <Card className="bg-white dark:bg-gray-800">
                <CardContent className="p-8 text-center">
                  <Target className="w-16 h-16 mx-auto mb-4 text-gray-400" />
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                    No picks found
                  </h3>
                  <p className="text-gray-600 dark:text-gray-400">
                    {selectedStatus === 'all' 
                      ? 'Start by making your first pick on a game!'
                      : `No ${selectedStatus} picks to display.`
                    }
                  </p>
                </CardContent>
              </Card>
            ) : (
              filteredPicks.map((pick: any) => (
                <Card key={pick.id} className="bg-white dark:bg-gray-800">
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          {getStatusBadge(pick.status)}
                          <span className="text-sm text-gray-500 dark:text-gray-400">
                            {new Date(pick.createdAt).toLocaleDateString()}
                          </span>
                        </div>

                        <h3 className="font-medium text-gray-900 dark:text-white mb-1">
                          {pick.awayTeam} @ {pick.homeTeam}
                        </h3>

                        <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                          {formatBet(pick)}
                        </p>

                        {pick.odds === 0 && pick.bookmaker === 'manual' && (
                          <div className="mt-2">
                            {editingOdds === pick.id ? (
                              <div className="flex items-center gap-2">
                                <Input
                                  type="text"
                                  value={tempOdds}
                                  onChange={(e) => setTempOdds(e.target.value)}
                                  placeholder="e.g., -110, +150"
                                  className="w-32"
                                />
                                <Button 
                                  size="sm" 
                                  onClick={() => handleSaveOdds(pick.id)}
                                  className="h-8 px-2"
                                >
                                  <Save className="w-3 h-3" />
                                </Button>
                                <Button 
                                  size="sm" 
                                  variant="outline" 
                                  onClick={handleCancelEdit}
                                  className="h-8 px-2"
                                >
                                  <X className="w-3 h-3" />
                                </Button>
                              </div>
                            ) : (
                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => handleEditOdds(pick.id, pick.odds)}
                                className="text-blue-600 border-blue-600 hover:bg-blue-50 dark:hover:bg-blue-950"
                              >
                                <Edit3 className="w-3 h-3 mr-1" />
                                Enter Odds
                              </Button>
                            )}
                          </div>
                        )}
                      </div>

                      <div className="flex items-center gap-2 ml-4">
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => deletePick(pick.id)}
                          className="text-red-600 border-red-600 hover:bg-red-50 dark:hover:bg-red-950"
                        >
                          <Trash2 className="w-3 h-3" />
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))
            )}
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}
